// allow importing .png files as StaticImageData
declare module '*.png' {
  import type { StaticImageData } from 'next/image';
  const value: StaticImageData;
  export default value;
}

// allow CSS modules
declare module '*.module.css';

'use client';

import { useTranslations } from 'next-intl';
import { useSession } from 'next-auth/react';
import { useRouter } from 'next/navigation';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import Image from 'next/image';
import { useState } from 'react';
import { UserProfile } from '@/types/UserProfile';
import { SUPPORTED_LOCALES, LOCALE_NAMES } from '@/config/i18n';

const profileSchema = z.object({
  fullName: z.string().min(2, 'Full name is required'),
  displayName: z.string().optional(),
  bio: z.string().max(500).optional(),
  location: z
    .object({
      country: z.string(),
      city: z.string().optional(),
      timezone: z.string(),
    })
    .optional(),
  phoneNumber: z.string().optional(),
  preferredLanguage: z.enum([...SUPPORTED_LOCALES] as unknown as [string, ...string[]]),
  profession: z.string().optional(),
  company: z.string().optional(),
  interests: z.array(z.string()).optional(),
  socialMedia: z
    .array(
      z.object({
        platform: z.enum(['twitter', 'linkedin', 'facebook', 'instagram']),
        url: z.string().url(),
      })
    )
    .optional(),
  avatar: z.string().optional(),
  privacySettings: z.object({
    profileVisibility: z.enum(['public', 'private', 'connections']),
    showEmail: z.boolean(),
    showPhone: z.boolean(),
    showLocation: z.boolean(),
  }),
  communicationPreferences: z.object({
    email: z.boolean(),
    sms: z.boolean(),
    inApp: z.boolean(),
  }),
});

type ProfileFormData = z.infer<typeof profileSchema>;

export default function ProfilePage() {
  const t = useTranslations('Profile');
  const { data: session } = useSession();
  const router = useRouter();
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [avatarPreview, setAvatarPreview] = useState('');

  const {
    register,
    handleSubmit,
    watch,
    setValue,
    formState: { errors },
  } = useForm<ProfileFormData>({
    resolver: zodResolver(profileSchema),
    defaultValues: async () => {
      try {
        const response = await fetch('/api/user/profile');
        if (!response.ok) throw new Error('Failed to fetch profile');
        const profile: UserProfile = await response.json();

        return {
          fullName: profile.fullName || '',
          displayName: profile.displayName || '',
          bio: profile.bio || '',
          location: profile.location || { country: '', timezone: '' },
          phoneNumber: profile.phoneNumber || '',
          preferredLanguage: profile.preferredLanguage || 'en',
          profession: profile.profession || '',
          company: profile.company || '',
          interests: profile.interests || [],
          socialMedia: profile.socialMedia || [],
          avatar: profile.avatar || '',
          privacySettings: profile.privacySettings || {
            profileVisibility: 'public',
            showEmail: true,
            showPhone: false,
            showLocation: false,
          },
          communicationPreferences: profile.communicationPreferences || {
            email: true,
            sms: false,
            inApp: true,
          },
        };
      } catch (err) {
        setError('Failed to load profile data');
        return {
          fullName: '',
          displayName: '',
          bio: '',
          location: { country: '', timezone: '' },
          phoneNumber: '',
          preferredLanguage: 'en',
          profession: '',
          company: '',
          interests: [],
          socialMedia: [],
          avatar: '',
          privacySettings: {
            profileVisibility: 'public',
            showEmail: true,
            showPhone: false,
            showLocation: false,
          },
          communicationPreferences: {
            email: true,
            sms: false,
            inApp: true,
          },
        };
      }
    },
  });

  const onSubmit = async (data: ProfileFormData) => {
    setIsLoading(true);
    setError('');

    try {
      const response = await fetch('/api/user/profile', {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        throw new Error('Failed to update profile');
      }

      const currentLang = watch('preferredLanguage');
      if (data.preferredLanguage !== currentLang) {
        router.refresh();
      }
    } catch (err: unknown) {
      if (err instanceof Error) {
        setError(err.message);
      } else {
        setError('Unexpected error');
      }
    } finally {
      setIsLoading(false);
    }
  };

  const handleAvatarChange = async (
    e: React.ChangeEvent<HTMLInputElement>
  ) => {
    const file = e.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onloadend = () => {
      setAvatarPreview(reader.result as string);
    };
    reader.readAsDataURL(file);

    const formData = new FormData();
    formData.append('avatar', file);

    try {
      const response = await fetch('/api/user/avatar', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Failed to upload avatar');
      }

      const { avatarUrl } = await response.json();
      setValue('avatar', avatarUrl);
    } catch {
      setError('Failed to upload avatar');
    }
  };

  if (!session) {
    return <div className="text-center py-10">{t('notSignedIn')}</div>;
  }

  return (
    <div className="max-w-4xl mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">{t('title')}</h1>

      {error && (
        <div className="mb-4 p-3 bg-red-50 text-red-700 rounded-md">{error}</div>
      )}

      <form onSubmit={handleSubmit(onSubmit)} className="space-y-8">
        {/* Avatar Section */}
        <div className="space-y-4">
          <label className="block text-sm font-medium text-gray-700">
            {t('avatar')}
          </label>
          <div className="flex items-center space-x-6">
            <div className="relative h-24 w-24">
              <Image
                src={
                  typeof watch('avatar') === 'string'
                    ? watch('avatar') || avatarPreview || '/default-avatar.png'
                    : '/default-avatar.png'
                }
                alt={t('avatar')}
                className="rounded-full object-cover"
                fill
              />
            </div>
            <input
              type="file"
              accept="image/*"
              onChange={handleAvatarChange}
              aria-label={t('avatar')}
              className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
            />
          </div>
        </div>

        {/* Full Name and Display Name */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label className="block text-sm font-medium text-gray-700">
              {t('fullName')}
            </label>
            <input
              type="text"
              placeholder={t('fullName')}
              {...register('fullName')}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
            />
            {errors.fullName && (
              <p className="mt-1 text-sm text-red-600">
                {errors.fullName.message}
              </p>
            )}
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">
              {t('displayName')}
            </label>
            <input
              type="text"
              placeholder={t('displayName')}
              {...register('displayName')}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
            />
          </div>
        </div>

        {/* Language Preference */}
        <div>
          <label className="block text-sm font-medium text-gray-700">
            {t('preferredLanguage')}
          </label>
          <select
            {...register('preferredLanguage')}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
          >
            {SUPPORTED_LOCALES.map((locale) => (
              <option key={locale} value={locale}>
                {LOCALE_NAMES[locale]}
              </option>
            ))}
          </select>
        </div>

        {/* Bio */}
        <div>
          <label className="block text-sm font-medium text-gray-700">
            {t('bio')}
          </label>
                   <textarea
            {...register('bio')}
            rows={4}
            placeholder={t('bio')}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
          />
          {errors.bio && (
            <p className="mt-1 text-sm text-red-600">{errors.bio.message}</p>
          )}
        </div>

        {/* Privacy Settings */}
        <div>
          <h3 className="text-lg font-medium text-gray-900 mb-4">
            {t('privacySettings')}
          </h3>
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">
                {t('profileVisibility')}
              </label>
              <select
                {...register('privacySettings.profileVisibility')}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              >
                <option value="public">{t('public')}</option>
                <option value="private">{t('private')}</option>
                <option value="connections">{t('connectionsOnly')}</option>
              </select>
            </div>

            <div className="space-y-2">
              <div className="flex items-center">
                <input
                  type="checkbox"
                  {...register('privacySettings.showEmail')}
                  className="h-4 w-4 rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <label className="ml-2 block text-sm text-gray-700">
                  {t('showEmail')}
                </label>
              </div>

              <div className="flex items-center">
                <input
                  type="checkbox"
                  {...register('privacySettings.showPhone')}
                  className="h-4 w-4 rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <label className="ml-2 block text-sm text-gray-700">
                  {t('showPhone')}
                </label>
              </div>

              <div className="flex items-center">
                <input
                  type="checkbox"
                  {...register('privacySettings.showLocation')}
                  className="h-4 w-4 rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <label className="ml-2 block text-sm text-gray-700">
                  {t('showLocation')}
                </label>
              </div>
            </div>
          </div>
        </div>

        {/* Communication Preferences */}
        <div>
          <h3 className="text-lg font-medium text-gray-900 mb-4">
            {t('communicationPreferences')}
          </h3>
          <div className="space-y-2">
            <div className="flex items-center">
              <input
                type="checkbox"
                {...register('communicationPreferences.email')}
                className="h-4 w-4 rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <label className="ml-2 block text-sm text-gray-700">
                {t('email')}
              </label>
            </div>

            <div className="flex items-center">
              <input
                type="checkbox"
                {...register('communicationPreferences.sms')}
                className="h-4 w-4 rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <label className="ml-2 block text-sm text-gray-700">
                {t('sms')}
              </label>
            </div>

            <div className="flex items-center">
              <input
                type="checkbox"
                {...register('communicationPreferences.inApp')}
                className="h-4 w-4 rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <label className="ml-2 block text-sm text-gray-700">
                {t('inApp')}
              </label>
            </div>
          </div>
        </div>

        {/* Submit Button */}
        <div className="flex justify-end">
          <button
            type="submit"
            disabled={isLoading}
            className="px-4 py-2 text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 rounded-md focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50"
          >
            {isLoading ? t('saving') : t('saveChanges')}
          </button>
        </div>
      </form>
    </div>
  );
}

'use client';

import { useState } from 'react';
import { useRouter } from 'next/navigation';
import { useForm } from 'react-hook-form';
import { z } from 'zod';
import { zodResolver } from '@hookform/resolvers/zod';

const announcementSchema = z.object({
  title: z.string().min(5, 'Title must be at least 5 characters').max(100, 'Title must not exceed 100 characters'),
  message: z.string().min(10, 'Message must be at least 10 characters').max(5000, 'Message must not exceed 5000 characters'),
  priority: z.enum(['low', 'medium', 'high']),
});

type AnnouncementForm = z.infer<typeof announcementSchema>;

export default function CreateAnnouncementPage() {
  const router = useRouter();
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm<AnnouncementForm>({
    resolver: zodResolver(announcementSchema),
    defaultValues: {
      priority: 'low',
    },
  });

  const onSubmit = async (data: AnnouncementForm) => {
    setIsSubmitting(true);
    setError('');
    setSuccess(false);

    try {
      const response = await fetch('/api/admin/announcements', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
      });

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.error || 'Failed to create announcement');
      }

      setSuccess(true);
      reset();
    } catch (err: unknown) {
      if (err instanceof Error) {
        setError(err.message);
      } else {
        setError('Unexpected error occurred');
      }
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto mt-10 p-6 bg-white rounded-lg shadow-md">
      <h1 className="text-2xl font-bold text-center mb-6">Create New Announcement</h1>

      {error && (
        <div className="mb-4 p-3 bg-red-50 text-red-700 rounded-md">
          {error}
        </div>
      )}

      {success && (
        <div className="mb-4 p-3 bg-green-50 text-green-700 rounded-md">
          Announcement sent successfully!
        </div>
      )}

      <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
        <div>
          <label htmlFor="title" className="block text-sm font-medium text-gray-700">
            Title
          </label>
          <input
            id="title"
            type="text"
            {...register('title')}
            placeholder="Enter announcement title"
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
          />
          {errors.title && <p className="mt-1 text-sm text-red-600">{errors.title.message}</p>}
        </div>

        <div>
          <label htmlFor="message" className="block text-sm font-medium text-gray-700">
            Message
          </label>
          <textarea
            id="message"
            {...register('message')}
            rows={6}
            placeholder="Enter announcement message"
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
          />
          {errors.message && <p className="mt-1 text-sm text-red-600">{errors.message.message}</p>}
        </div>

        <div>
          <label htmlFor="priority" className="block text-sm font-medium text-gray-700">
            Priority
          </label>
          <select
            id="priority"
            {...register('priority')}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
          >
            <option value="low">Low</option>
            <option value="medium">Medium</option>
            <option value="high">High</option>
          </select>
          {errors.priority && <p className="mt-1 text-sm text-red-600">{errors.priority.message}</p>}
        </div>

        <div className="flex items-center justify-end space-x-4">
          <button
            type="button"
            onClick={() => reset()}
            className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 hover:bg-gray-200 rounded-md focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-500"
          >
            Reset
          </button>
          <button
            type="submit"
            disabled={isSubmitting}
            className="px-4 py-2 text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 rounded-md focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50"
          >
            {isSubmitting ? 'Sending...' : 'Send Announcement'}
          </button>
        </div>
      </form>
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';

interface Announcement {
  id: string;
  title: string;
  message: string;
  createdAt: string;
  createdBy: string;
  priority: 'low' | 'medium' | 'high';
}

interface AnnouncementResponse {
  announcements: Announcement[];
  total: number;
  page: number;
  limit: number;
}

export default function ViewAnnouncements() {
  const [announcements, setAnnouncements] = useState<Announcement[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);

  useEffect(() => {
    const fetchAnnouncements = async () => {
      try {
        const response = await fetch(`/api/admin/announcements?page=${page}`);
        if (!response.ok) {
          throw new Error('Failed to fetch announcements');
        }

        const data: AnnouncementResponse = await response.json();

        setAnnouncements((prev) =>
          page === 1 ? data.announcements : [...prev, ...data.announcements]
        );
        setHasMore(data.announcements.length === data.limit);
      } catch (err: unknown) {
        if (err instanceof Error) {
          setError(err.message);
        } else {
          setError('Unexpected error occurred');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchAnnouncements();
  }, [page]);

  const getPriorityColor = (priority: Announcement['priority']) => {
    switch (priority) {
      case 'high':
        return 'bg-red-100 text-red-800';
      case 'medium':
        return 'bg-yellow-100 text-yellow-800';
      case 'low':
        return 'bg-green-100 text-green-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="max-w-4xl mx-auto mt-10 p-6">
      <h1 className="text-2xl font-bold text-center mb-6">Announcements</h1>

      {error && (
        <div className="mb-4 p-3 bg-red-50 text-red-700 rounded-md">{error}</div>
      )}

      {loading && page === 1 ? (
        <div className="animate-pulse space-y-4">
          {[1, 2, 3].map((i) => (
            <div key={i} className="bg-gray-100 p-6 rounded-lg">
              <div className="h-4 bg-gray-200 rounded w-1/4 mb-4"></div>
              <div className="h-4 bg-gray-200 rounded w-full mb-2"></div>
              <div className="h-4 bg-gray-200 rounded w-3/4"></div>
            </div>
          ))}
        </div>
      ) : (
        <div className="space-y-6">
          {announcements.map((announcement) => (
            <div
              key={announcement.id}
              className="bg-white p-6 rounded-lg shadow-md"
            >
              <div className="flex items-center justify-between mb-4">
                <h2 className="text-xl font-semibold">{announcement.title}</h2>
                <span
                  className={`px-2 py-1 rounded-full text-xs font-medium ${getPriorityColor(
                    announcement.priority
                  )}`}
                >
                  {announcement.priority.toUpperCase()}
                </span>
              </div>

              <p className="text-gray-600 whitespace-pre-wrap mb-4">
                {announcement.message}
              </p>

              <div className="flex items-center justify-between text-sm text-gray-500">
                <span>By: {announcement.createdBy}</span>
                <span>
                  {new Date(announcement.createdAt).toLocaleDateString()}
                </span>
              </div>
            </div>
          ))}

          {hasMore && (
            <div className="text-center">
              <button
                onClick={() => setPage((p) => p + 1)}
                disabled={loading}
                className="px-4 py-2 text-sm font-medium text-blue-600 bg-white hover:bg-blue-50 rounded-md border border-blue-300 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50"
              >
                {loading ? 'Loading...' : 'Load More'}
              </button>
            </div>
          )}

          {!hasMore && announcements.length > 0 && (
            <p className="text-center text-gray-500">
              No more announcements to load.
            </p>
          )}

          {!loading && announcements.length === 0 && (
            <div className="text-center text-gray-500">
              No announcements yet.
            </div>
          )}
        </div>
      )}
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { usePermissions } from '@/hooks/usePermissions';
import { PermissionErrorFallback } from '@/components/errors/PermissionErrorFallback';

interface AdminCode {
  email: string;
  code: string;
  createdAt: string;
  lastUsed?: string;
}

export default function AdminPage() {
  const { hasPermission } = usePermissions();
  const [email, setEmail] = useState('');
  const [loading, setLoading] = useState(false);
  const [adminCodes, setAdminCodes] = useState<AdminCode[]>([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Only super admins can access this page
  if (!hasPermission('admin:manage')) {
    return <PermissionErrorFallback scope="admin:manage" />;
  }

  useEffect(() => {
    fetchAdminCodes();
  }, []);

  const fetchAdminCodes = async () => {
    try {
      const response = await fetch('/api/admin/codes');
      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Failed to fetch admin codes');
      }

      setAdminCodes(data);
    } catch (err: unknown) {
      setError(err instanceof Error ? err.message : 'Unexpected error');
    }
  };

  const handleCreateAdmin = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      const response = await fetch('/api/admin/codes', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Failed to create admin code');
      }

      setSuccess(`Admin code created and sent to ${email}`);
      setEmail('');
      fetchAdminCodes();
    } catch (err: unknown) {
      setError(err instanceof Error ? err.message : 'Unexpected error');
    } finally {
      setLoading(false);
    }
  };

  const handleRevokeCode = async (code: string) => {
    if (!confirm('Are you sure you want to revoke this admin code?')) return;

    try {
      const response = await fetch(`/api/admin/codes/${code}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error('Failed to revoke admin code');
      }

      setSuccess('Admin code revoked successfully');
      fetchAdminCodes();
    } catch (err: unknown) {
      setError(err instanceof Error ? err.message : 'Unexpected error');
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">Admin Management</h1>

      {/* Create Admin Form */}
      <div className="bg-white p-6 rounded-lg shadow-md mb-8">
        <h2 className="text-xl font-semibold mb-4">Create New Admin</h2>
        <form onSubmit={handleCreateAdmin} className="space-y-4">
          <div>
            <label htmlFor="email" className="block text-sm font-medium text-gray-700">
              Admin Email
            </label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
              required
            />
          </div>
          <button
            type="submit"
            disabled={loading}
            className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700 disabled:opacity-50"
          >
            {loading ? 'Creating...' : 'Create Admin Code'}
          </button>
        </form>
      </div>

      {/* Status Messages */}
      {error && (
        <div className="bg-red-50 text-red-700 p-4 rounded-md mb-4">{error}</div>
      )}
      {success && (
        <div className="bg-green-50 text-green-700 p-4 rounded-md mb-4">{success}</div>
      )}

      {/* Admin Codes List */}
      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-semibold mb-4">Active Admin Codes</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead>
              <tr>
                {['Email', 'Code', 'Created At', 'Last Used', ''].map((header) => (
                  <th
                    key={header}
                    className="px-6 py-3 bg-gray-50 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                  >
                    {header}
                  </th>
                ))}
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {adminCodes.map((admin) => (
                <tr key={admin.code}>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {admin.email}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-mono text-gray-900">
                    {admin.code}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {new Date(admin.createdAt).toLocaleDateString()}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {admin.lastUsed ? new Date(admin.lastUsed).toLocaleDateString() : 'Never'}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                    <button
                      onClick={() => handleRevokeCode(admin.code)}
                      className="text-red-600 hover:text-red-900"
                    >
                      Revoke
                    </button>
                  </td>
                </tr>
              ))}
              {adminCodes.length === 0 && (
                <tr>
                  <td colSpan={5} className="px-6 py-4 text-center text-sm text-gray-500">
                    No admin codes available.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

'use client';

import { useState } from 'react';
import { useRouter } from 'next/navigation';
import { zodResolver } from '@hookform/resolvers/zod';
import { useForm } from 'react-hook-form';
import { z } from 'zod';

const adminRegistrationSchema = z.object({
  email: z.string().email('Invalid email address'),
  fullName: z.string().min(2, 'Full name is required'),
  password: z
    .string()
    .min(8, 'Password must be at least 8 characters')
    .regex(/[A-Z]/, 'Must contain at least one uppercase letter')
    .regex(/[a-z]/, 'Must contain at least one lowercase letter')
    .regex(/[0-9]/, 'Must contain at least one number')
    .regex(/[^A-Za-z0-9]/, 'Must contain at least one special character'),
  acceptTerms: z.literal(true, {
    message: 'You must accept the terms and conditions',
  }),
});

type AdminRegistrationForm = z.infer<typeof adminRegistrationSchema>;

export default function AdminRegistration() {
  const router = useRouter();
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [verificationSent, setVerificationSent] = useState(false);

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<AdminRegistrationForm>({
    resolver: zodResolver(adminRegistrationSchema),
  });

  const onSubmit = async (data: AdminRegistrationForm) => {
    setIsSubmitting(true);
    setError('');

    try {
      const response = await fetch('/api/admin/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
      });

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.error || 'Registration failed');
      }

      setVerificationSent(true);
    } catch (err: unknown) {
      setError(err instanceof Error ? err.message : 'Unexpected error');
    } finally {
      setIsSubmitting(false);
    }
  };

  if (verificationSent) {
    return (
      <div className="max-w-md mx-auto mt-10 p-6 bg-white rounded-lg shadow-md">
        <h2 className="text-2xl font-bold text-center text-green-600 mb-4">
          ✉️ Verification Email Sent
        </h2>
        <p className="text-gray-600 text-center">
          We've sent a verification email to your address. Please check your inbox
          and click the verification link to complete your registration.
        </p>
        <div className="mt-6 text-center text-sm text-gray-500">
          Didn’t receive the email?{' '}
          <button
            onClick={() => window.location.reload()}
            className="text-blue-600 hover:underline"
          >
            Try again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white rounded-lg shadow-md">
      <h1 className="text-2xl font-bold text-center mb-6">Admin Registration</h1>

      {error && (
        <div className="mb-4 p-3 bg-red-50 text-red-700 rounded-md">{error}</div>
      )}

      <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
        <div>
          <label htmlFor="email" className="block text-sm font-medium text-gray-700">
            Email Address
          </label>
          <input
            id="email"
            type="email"
            {...register('email')}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
          />
          {errors.email && (
            <p className="mt-1 text-sm text-red-600">{errors.email.message}</p>
          )}
        </div>

        <div>
          <label htmlFor="fullName" className="block text-sm font-medium text-gray-700">
            Full Name
          </label>
          <input
            id="fullName"
            type="text"
            {...register('fullName')}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
          />
          {errors.fullName && (
            <p className="mt-1 text-sm text-red-600">{errors.fullName.message}</p>
          )}
        </div>

        <div>
          <label htmlFor="password" className="block text-sm font-medium text-gray-700">
            Password
          </label>
          <input
            id="password"
            type="password"
            {...register('password')}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
          />
          {errors.password && (
            <p className="mt-1 text-sm text-red-600">{errors.password.message}</p>
          )}
        </div>

        <div className="flex items-start">
          <div className="flex items-center h-5">
            <input
              id="acceptTerms"
              type="checkbox"
              {...register('acceptTerms')}
              className="h-4 w-4 rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
          </div>
          <div className="ml-3 text-sm">
            <label htmlFor="acceptTerms" className="font-medium text-gray-700">
              I accept the{' '}
              <a
                href="/admin/terms"
                target="_blank"
                className="text-blue-600 hover:underline"
              >
                terms and conditions
              </a>
            </label>
            {errors.acceptTerms && (
              <p className="mt-1 text-sm text-red-600">{errors.acceptTerms.message}</p>
            )}
          </div>
        </div>

        <button
          type="submit"
          disabled={isSubmitting}
          className="w-full py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50"
        >
          {isSubmitting ? 'Registering...' : 'Register as Admin'}
        </button>
      </form>
    </div>
  );
}

import { NextRequest } from 'next/server';
import { z } from 'zod';
import {
  createAnnouncement,
  getAnnouncements,
  getAllUserEmails,
  queueAnnouncementEmail,
  processEmailQueue,
} from '@/lib/services/announcements';
import { getServerSession } from 'next-auth';

const announcementSchema = z.object({
  title: z.string().min(5, 'Title must be at least 5 characters').max(100, 'Title must not exceed 100 characters'),
  message: z.string().min(10, 'Message must be at least 10 characters').max(5000, 'Message must not exceed 5000 characters'),
  priority: z.enum(['low', 'medium', 'high']),
});

export async function POST(request: NextRequest) {
  try {
    const session = await getServerSession();
    if (!session?.user?.email) {
      return new Response(JSON.stringify({ error: 'Unauthorized' }), {
        status: 401,
        headers: { 'Content-Type': 'application/json' },
      });
    }

    const body = await request.json();
    const validatedData = announcementSchema.parse(body);

    const announcement = await createAnnouncement({
      ...validatedData,
      createdBy: session.user.email,
    });

    const userEmails = await getAllUserEmails();

    await Promise.all(
      userEmails.map((email) =>
        queueAnnouncementEmail({
          to: email,
          ...announcement,
          announcementId: announcement.id,
        })
      )
    );

    processEmailQueue().catch((err) => {
      console.error('Email queue processing failed:', err);
    });

    return new Response(
      JSON.stringify({
        message: 'Announcement created and notifications sent',
        announcement,
      }),
      {
        status: 200,
        headers: { 'Content-Type': 'application/json' },
      }
    );
  } catch (error: unknown) {
    if (error instanceof z.ZodError) {
      return new Response(
        JSON.stringify({
          error: 'Validation failed',
          details: error.issues,
        }),
        {
          status: 400,
          headers: { 'Content-Type': 'application/json' },
        }
      );
    }

    console.error('Failed to create announcement:', error);
    return new Response(
      JSON.stringify({ error: 'Failed to create announcement' }),
      {
        status: 500,
        headers: { 'Content-Type': 'application/json' },
      }
    );
  }
}

export async function GET(request: NextRequest) {
  try {
    const session = await getServerSession();
    if (!session?.user) {
      return new Response(JSON.stringify({ error: 'Unauthorized' }), {
        status: 401,
        headers: { 'Content-Type': 'application/json' },
      });
    }

    const { searchParams } = new URL(request.url);
    const page = parseInt(searchParams.get('page') || '1', 10);
    const limit = parseInt(searchParams.get('limit') || '10', 10);

    const result = await getAnnouncements(page, limit);

    return new Response(JSON.stringify(result), {
      status: 200,
      headers: { 'Content-Type': 'application/json' },
    });
  } catch (error: unknown) {
    console.error('Failed to fetch announcements:', error);
    return new Response(JSON.stringify({ error: 'Failed to fetch announcements' }), {
      status: 500,
      headers: { 'Content-Type': 'application/json' },
    });
  }
}

import { NextResponse } from 'next/server';
import { AdminService } from '@/lib/services/admin';
import { logger } from '@/lib/logger';
import { getServerSession } from 'next-auth';

export async function DELETE(
  request: Request,
  { params }: { params: { code: string } }
) {
  try {
    const session = await getServerSession();
    const role = session?.user?.role || 'GUEST';

    const success = await AdminService.revokeCode(params.code, role);

    if (!success) {
      return NextResponse.json(
        { error: 'Admin code not found' },
        { status: 404 }
      );
    }

    return NextResponse.json(
      { message: 'Admin code revoked successfully' },
      { status: 200 }
    );
  } catch (error: unknown) {
    logger.error(
      {
        error: error instanceof Error ? error.message : String(error),
        code: params.code,
      },
      'Failed to revoke admin code'
    );

    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    );
  }
}

import { NextResponse } from 'next/server';
import { AdminService } from '@/lib/services/admin';
import { checkRateLimit } from '@/lib/rateLimit';
import { logger } from '@/lib/logger';

export async function POST(request: Request) {
  try {
    const ip = request.headers.get('x-forwarded-for') || '127.0.0.1';
    const rateLimit = await checkRateLimit(ip, 'API');

    if (!rateLimit.success) {
      return NextResponse.json({ error: rateLimit.message }, { status: 429 });
    }

    const { email } = await request.json();

    if (!email || typeof email !== 'string') {
      return NextResponse.json(
        { error: 'Email is required and must be a string' },
        { status: 400 }
      );
    }

    const adminCode = await AdminService.createAdminCode(email);

    return NextResponse.json(
      {
        message: 'Admin code created successfully',
        adminCode,
      },
      { status: 200 }
    );
  } catch (error: unknown) {
    logger.error(
      { error: error instanceof Error ? error.message : String(error) },
      'Failed to create admin code'
    );

    if (
      typeof error === 'object' &&
      error !== null &&
      'code' in error &&
      (error as any).code === 'MAX_ADMINS_REACHED'
    ) {
      return NextResponse.json(
        { error: (error as any).message || 'Max admins reached' },
        { status: 403 }
      );
    }

    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    );
  }
}

export async function GET() {
  try {
    const adminCodes = await AdminService.listAdminCodes();
    return NextResponse.json(adminCodes, { status: 200 });
  } catch (error: unknown) {
    logger.error(
      { error: error instanceof Error ? error.message : String(error) },
      'Failed to list admin codes'
    );
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    );
  }
}

import { NextRequest } from 'next/server';
import { z } from 'zod';
import { createAdminUser } from '@/lib/services/admin';
import { sendVerificationEmail } from '@/lib/email/templates/admin-verification';

const adminRegistrationSchema = z.object({
  email: z.string().email('Invalid email address'),
  fullName: z.string().min(2, 'Full name is required'),
  password: z
    .string()
    .min(8, 'Password must be at least 8 characters')
    .regex(/[A-Z]/, 'Password must contain at least one uppercase letter')
    .regex(/[a-z]/, 'Password must contain at least one lowercase letter')
    .regex(/[0-9]/, 'Password must contain at least one number')
    .regex(/[^A-Za-z0-9]/, 'Password must contain at least one special character'),
  acceptTerms: z.literal(true, {
    message: 'You must accept the terms and conditions',
  }),
});

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    const validatedData = adminRegistrationSchema.parse(body);

    const { verificationCode } = await createAdminUser(
      {
        email: validatedData.email,
        fullName: validatedData.fullName,
        password: validatedData.password,
      },
      'ADMIN' // ✅ Required second argument
    );

    await sendVerificationEmail(validatedData.email, verificationCode);

    return new Response(
      JSON.stringify({
        message: 'Registration successful. Please check your email for verification.',
      }),
      {
        status: 200,
        headers: { 'Content-Type': 'application/json' },
      }
    );
  } catch (error: unknown) {
    if (error instanceof z.ZodError) {
      return new Response(
        JSON.stringify({
          error: 'Validation failed',
          details: error.issues,
        }),
        {
          status: 400,
          headers: { 'Content-Type': 'application/json' },
        }
      );
    }

    if (
      typeof error === 'object' &&
      error !== null &&
      'code' in error &&
      (error as any).code === 'MAX_ADMINS_REACHED'
    ) {
      return new Response(
        JSON.stringify({
          error: 'Maximum number of admins reached',
        }),
        {
          status: 403,
          headers: { 'Content-Type': 'application/json' },
        }
      );
    }

    if (
      typeof error === 'object' &&
      error !== null &&
      'code' in error &&
      (error as any).code === 'EMAIL_ALREADY_EXISTS'
    ) {
      return new Response(
        JSON.stringify({
          error: 'An account with this email already exists',
        }),
        {
          status: 409,
          headers: { 'Content-Type': 'application/json' },
        }
      );
    }

    console.error('Admin registration error:', error);
    return new Response(
      JSON.stringify({
        error: 'Registration failed. Please try again later.',
      }),
      {
        status: 500,
        headers: { 'Content-Type': 'application/json' },
      }
    );
  }
}

