"use client";

import { useState } from "react";
import { useRouter, useSearchParams } from "next/navigation";
import {
  getAuth,
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  sendEmailVerification,
  sendPasswordResetEmail,
} from "firebase/auth";
import {
  getFirestore,
  doc,
  setDoc,
  getDoc,
  serverTimestamp,
} from "firebase/firestore";
import { app } from "@/firebase/config";
import { getErrorMessage } from "@/lib/errorUtils";

export default function LoginPage() {
  const auth = getAuth(app);
  const db = getFirestore(app);
  const router = useRouter();
  const searchParams = useSearchParams();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [info, setInfo] = useState("");
  const [isSignUp, setIsSignUp] = useState(false);
  const [loading, setLoading] = useState(false);
  const [showReset, setShowReset] = useState(false);

  const handleAuth = async (e: React.FormEvent) => {
    e.preventDefault();
    setError("");
    setInfo("");
    setLoading(true);

    try {
      if (email && password) {
        if (isSignUp) {
          const referrer = searchParams?.get("ref");
          if (referrer && referrer.toLowerCase() === email.toLowerCase()) {
            setError("You cannot refer yourself.");
            setLoading(false);
            return;
          }

          let referredBy: string | null = null;
          if (referrer) {
            const refSnap = await getDoc(doc(db, "users", referrer));
            if (refSnap.exists()) referredBy = referrer;
            else {
              setError("Invalid referral code.");
              setLoading(false);
              return;
            }
          }

          const userCredential = await createUserWithEmailAndPassword(auth, email, password);
          const user = userCredential.user;

          await setDoc(doc(db, "users", user.uid), {
            uid: user.uid,
            email: user.email,
            role: "basic",
            referredBy,
            createdAt: serverTimestamp(),
          });

          await sendEmailVerification(user);
          setInfo("Account created! Please check your email for verification.");
          setLoading(false);
          return;
        } else {
          const userCredential = await signInWithEmailAndPassword(auth, email, password);
          const user = userCredential.user;

          if (!user.emailVerified) {
            setError("Please verify your email before logging in.");
            setLoading(false);
            return;
          }
        }
        router.push("/dashboard");
      } else {
        setError("Please enter both email and password.");
      }
    } catch (err: unknown) {
      if (err && typeof err === "object" && "code" in err) {
        setError(
          getErrorMessage(
            (err as { code?: string; message?: string }).code ||
            (err as { message?: string }).message || ""
          )
        );
      } else {
        setError("An error occurred. Please try again.");
      }
    } finally {
      setLoading(false);
    }
  };

  const handleResetPassword = async (e: React.FormEvent) => {
    e.preventDefault();
    setError("");
    setInfo("");
    setLoading(true);

    try {
      if (!email) {
        setError("Please enter your email to reset password.");
        setLoading(false);
        return;
      }
      await sendPasswordResetEmail(auth, email);
      setInfo("Password reset email sent! Please check your inbox.");
    } catch (err: unknown) {
      if (err && typeof err === "object" && "code" in err) {
        setError(
          getErrorMessage(
            (err as { code?: string; message?: string }).code ||
            (err as { message?: string }).message || ""
          )
        );
      } else {
        setError("An error occurred. Please try again.");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-100 via-white to-indigo-200 flex items-center justify-center px-4">
      <form
        onSubmit={showReset ? handleResetPassword : handleAuth}
        className="w-full max-w-md p-6 bg-white rounded-lg shadow-md hover:shadow-lg transition-shadow duration-300"
        aria-label={isSignUp ? "Sign up form" : "Login form"}
      >
        <div className="text-center mb-6">
          <h1 className="text-3xl font-extrabold text-indigo-700">Pulse Connect</h1>
          <p className="text-gray-500 text-sm mt-1">Your digital marketing command center</p>
        </div>

        <h2 className="text-2xl font-bold mb-6 text-center">
          {showReset
            ? "Reset Your Password"
            : isSignUp
            ? "Create a Pulse Connect Account"
            : "Sign In to Pulse Connect"}
        </h2>

        <label htmlFor="email" className="sr-only">Email</label>
        <input
          id="email"
          name="email"
          type="email"
          placeholder="Email"
          value={email}
          autoComplete="email"
          onChange={(e) => setEmail(e.target.value)}
          className="w-full mb-4 px-4 py-2 border rounded"
          required
        />

        {!showReset && (
          <>
            <label htmlFor="password" className="sr-only">Password</label>
            {isSignUp ? (
              <input
                id="password"
                name="password"
                type="password"
                placeholder="Password"
                value={password}
                autoComplete="new-password"
                onChange={(e) => setPassword(e.target.value)}
                className="w-full mb-4 px-4 py-2 border rounded"
                required
              />
            ) : (
              <input
                id="password"
                name="password"
                type="password"
                placeholder="Password"
                value={password}
                autoComplete="current-password"
                onChange={(e) => setPassword(e.target.value)}
                className="w-full mb-4 px-4 py-2 border rounded"
                required
              />
            )}
          </>
        )}

        {(error || info) && (
          <p
            className={`mb-4 text-sm ${error ? "text-red-500" : "text-green-600"}`}
            role="alert"
          >
            {error || info}
          </p>
        )}

        <button
          type="submit"
          className="w-full bg-indigo-600 text-white py-2 px-4 rounded hover:bg-indigo-700 disabled:opacity-50"
          disabled={loading}
        >
          {loading
            ? "Processing..."
            : showReset
            ? "Send Reset Email"
            : isSignUp
            ? "Sign Up"
            : "Login"}
        </button>

        <div className="flex flex-col items-center mt-4 gap-2">
          {!showReset && (
            <button
              type="button"
              className="text-blue-600 underline text-sm"
              onClick={() => setShowReset(true)}
              disabled={loading}
            >
              Forgot password?
            </button>
          )}
          {showReset && (
            <button
              type="button"
              className="text-blue-600 underline text-sm"
              onClick={() => setShowReset(false)}
              disabled={loading}
            >
              Back to {isSignUp ? "Sign Up" : "Login"}
            </button>
          )}
        </div>

        <p className="text-sm text-center mt-4">
          {isSignUp ? "Already have an account?" : "Don't have an account?"}{" "}
          <button
            type="button"
            className="text-blue-600 underline"
            onClick={() => {
              setIsSignUp(!isSignUp);
              setShowReset(false);
              setError("");
              setInfo("");
            }}
            disabled={loading}
          >
            {isSignUp ? "Log in" : "Sign up"}
          </button>
        </p>
      </form>
    </div>
  );
}

import { NextResponse } from 'next/server';
import { headers } from 'next/headers';
import { verifyStripeWebhook } from '@/lib/services/payment/stripe';
import { verifyPaystackPayment } from '@/lib/services/payment/paystack';
import { updateBookingStatus } from '@/lib/services/booking';
import { logger } from '@/lib/logger';

export async function POST(req: Request) {
  try {
    const headersList = headers(); // ✅ synchronous — no await
    const body = await req.text();
    const stripeSignature = headersList.get('stripe-signature');
    const paystackReference = headersList.get('x-paystack-reference');

    // 🔐 Stripe webhook handling
    if (stripeSignature) {
      const event = await verifyStripeWebhook(Buffer.from(body), stripeSignature);

      switch (event.type) {
        case 'checkout.session.completed': {
          const session = event.data.object;
          const bookingId = session.metadata?.bookingId;

          if (bookingId) {
            await updateBookingStatus(
              bookingId,
              'confirmed',
              session.payment_intent as string
            );
          }
          break;
        }

        case 'payment_intent.payment_failed': {
          const session = event.data.object;
          const bookingId = session.metadata?.bookingId;

          if (bookingId) {
            await updateBookingStatus(bookingId, 'cancelled');
          }
          break;
        }
      }
    }

    // 🔐 Paystack webhook handling
    if (paystackReference) {
      const isValid = await verifyPaystackPayment(paystackReference);

      if (isValid) {
        let webhookBody: any;
        try {
          webhookBody = JSON.parse(body);
        } catch (parseError) {
          logger.warn('Failed to parse Paystack webhook body:', parseError);
          return NextResponse.json({ error: 'Invalid JSON payload' }, { status: 400 });
        }

        const bookingId = webhookBody?.data?.metadata?.bookingId;

        if (bookingId) {
          await updateBookingStatus(
            bookingId,
            'confirmed',
            webhookBody.data.reference
          );
        }
      }
    }

    return NextResponse.json({ received: true }, { status: 200 });
  } catch (error: unknown) {
    logger.error(
      { error: error instanceof Error ? error.message : String(error) },
      'Webhook processing failed'
    );

    return NextResponse.json(
      { error: 'Webhook processing failed' },
      { status: 400 }
    );
  }
}

"use client";

import { useEffect, useState } from "react";
import { getAuth, signInAnonymously, onAuthStateChanged, User } from "firebase/auth";
import { app } from "@/firebase/config";

export default function FirebaseTest() {
  const [user, setUser] = useState<User | null>(null);
  const [status, setStatus] = useState("Connecting to Firebase...");

  useEffect(() => {
    const auth = getAuth(app);

    // Watch for auth state changes
    const unsubscribe = onAuthStateChanged(auth, (firebaseUser) => {
      if (firebaseUser) {
        setUser(firebaseUser);
        setStatus(`✅ Connected — UID: ${firebaseUser.uid}`);
      } else {
        setStatus("⚠️ No active session.");
      }
    });

    // Authenticate anonymously (for testing only)
    signInAnonymously(auth).catch((error) =>
      setStatus(`❌ Firebase error: ${error.message}`)
    );

    return () => unsubscribe();
  }, []);

  return (
    <div className="min-h-screen flex items-center justify-center bg-background text-foreground font-mono text-lg px-4">
      <div className="p-6 rounded-lg border border-foreground/10 shadow-sm bg-white/5 backdrop-blur max-w-md w-full text-center">
        <p className="mb-2">{status}</p>
        {user && (
          <pre className="text-sm text-foreground/60 break-words">
            User UID: {user.uid}
          </pre>
        )}
      </div>
    </div>
  );
}

