import { withPermission } from '@/lib/auth/withPermission';
import { useLanguage } from '../context/LanguageProvider';

const bundles = [
	{ name: 'Starter', price: 500, features: ['5 listings', '2 boosts', 'analytics'] },
	{ name: 'Pro', price: 1500, features: ['Unlimited listings', 'Priority support', 'Custom branding'] }
];

const AdminBundlesComponent: React.FC = () => {
	const locale = useLanguage().lang; // Add dynamic locale support

	return (
		<div>
			<h2>{locale === 'sw' ? 'Vifurushi vya Biashara' : 'Business Bundles'}</h2>
			{bundles.map((bundle) => (
				<div key={bundle.name}>
					<h3>
						{bundle.name}
						{locale === 'sw' ? `KES ${bundle.price}/mwezi` : `KES ${bundle.price}/month`}
					</h3>
					<ul>
						{bundle.features.map((f) => (
							<li key={f}>{f}</li>
						))}
					</ul>
				</div>
			))}
		</div>
	);
};

export const AdminBundles = withPermission(AdminBundlesComponent, {
	resource: 'admin',
	action: 'manage'
});

"use client";

import { useEffect, useState } from "react";
import { onAuthStateChanged } from "firebase/auth";
import { useRouter } from "next/navigation";
import { auth } from "@/firebase/config";

export default function AuthGate({ children }: { children: React.ReactNode }) {
  const [checking, setChecking] = useState(true);
  const router = useRouter();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (!user) {
        router.replace("/login");
      } else if (!user.emailVerified) {
        router.replace("/verify-email");
      } else {
        setChecking(false);
      }
    });

    return () => unsubscribe();
  }, [router]);

  if (checking) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-white" role="alert" aria-live="polite">
        <p className="text-indigo-600 text-lg font-medium animate-pulse">Checking credentials...</p>
      </div>
    );
  }

  return <>{children}</>;
}

// components/BoostListing.tsx
// [CLEANED] Removed redundant React import
import { usePulseCredits } from '../hooks/usePulseCredits';
import FeatureGate from "@/components/FeatureGate";

type Props = {
  listingId: string;
  userId: string;
};

export const BoostListing: React.FC<Props> = ({ listingId, userId }) => {
  const { balance, spendCredits } = usePulseCredits(userId);

  const handleBoost = async () => {
    try {
      if (balance >= 2) {
        await spendCredits(2, `Boost listing ${listingId}`);
        alert('Listing boosted with celebration!');
      } else {
        alert('Not enough credits. Please purchase more.');
      }
    } catch (error) {
      console.error("Boost failed:", error);
      alert("An error occurred while boosting the listing.");
    }
  };

  return (
    <FeatureGate permission="listings:boost">
      <button onClick={handleBoost}>
        🚀 Boost Listing (2 Credits)
      </button>
    </FeatureGate>
  );
};

import React, { useEffect } from "react";
import confetti from "canvas-confetti";
import { FeatureLabels } from "@/config/FeatureLabels";
import { useConfettiTheme } from "@/config/ConfettiTheme";

interface BrandCelebrationProps {
  features: string[];
  language: string;
  fundingSource: "user" | "org";
  onDone?: () => void;
}

export default function BrandCelebration({
  features,
  language,
  fundingSource,
  onDone
}: BrandCelebrationProps) {
  const regionalColors = useConfettiTheme();

  useEffect(() => {
    confetti({
      particleCount: 75,
      spread: 80,
      origin: { x: 0.5, y: 0.6 },
      angle: 90,
      gravity: 0.9,
      scalar: 1.2,
      ticks: 180,
      colors:
        fundingSource === "org"
          ? regionalColors
          : ["#FACC15", "#4ADE80", "#60A5FA"] // fallback for user-funded
    });

    const timer = setTimeout(() => {
      if (onDone) onDone();
    }, 3000);

    return () => clearTimeout(timer);
  }, [features, language, fundingSource, onDone, regionalColors]);

  const featureList = features
    .map((f) => FeatureLabels[language]?.[f] ?? FeatureLabels["en"]?.[f] ?? f)
    .join(", ");

  const message: string =
    fundingSource === "org"
      ? {
          en: `Your org unlocked: ${featureList}`,
          sw: `Shirika lako limefungua: ${featureList}`,
          yo: `Ẹgbẹ rẹ ti ṣii: ${featureList}`,
          ar: `منظمتك فتحت: ${featureList}`,
          hi: `आपकी संस्था ने अनलॉक किया है: ${featureList}`,
          pt: `Sua organização desbloqueou: ${featureList}`
        }[language] ?? `Your org unlocked: ${featureList}`
      : {
          en: `You unlocked: ${featureList}`,
          sw: `Umefungua: ${featureList}`,
          yo: `O ti ṣii: ${featureList}`,
          ar: `لقد فتحت: ${featureList}`,
          hi: `आपने अनलॉक किया है: ${featureList}`,
          pt: `Você desbloqueou: ${featureList}`
        }[language] ?? `You unlocked: ${featureList}`;

  return (
    <div className="flex items-center gap-3 bg-white rounded shadow-lg p-4 text-blue-900">
      <img src="/icon.png" alt="PulseConnect" className="w-6 h-6 animate-pulse" />
      <span className="text-sm font-medium">{message}</span>
    </div>
  );
}

import Image from "next/image";
import logo from "../assets/Pulse-Connect-logo.png";


import { useTheme } from "@/context/ThemeContext";
import clsx from "clsx";
import styles from "./BrandLogo.module.css";

export default function BrandLogo({
  width = 100,
  height = 100,
  className = "",
  animated = true,
  "aria-label": ariaLabel = "Pulse Connect Logo",
}: {
  width?: number;
  height?: number;
  className?: string;
  animated?: boolean;
  "aria-label"?: string;
}) {
  // Theme-aware border/shadow for personality
  const { theme } = useTheme?.() || { theme: "light" };
  return (
    <span
      className={clsx(
        "inline-block",
        theme === "dark"
          ? "bg-white/10 border-white/30 shadow-lg"
          : "bg-white border-gray-200 shadow",
        animated && styles["animate-logo-bounce"],
        "rounded-full border p-1",
        className
      )}
      aria-label={ariaLabel}
      role="img"
      tabIndex={0}
    >
      <Image
        src={logo}
        alt={ariaLabel}
        width={width}
        height={height}
        priority
        draggable={false}
        style={{
          filter: theme === "dark" ? "drop-shadow(0 0 2px #fff)" : undefined,
          transition: "transform 0.3s cubic-bezier(.4,2,.6,1)",
        }}
      />
    </span>
  );
}

