import { useEffect, useState } from "react";
import styles from "./DashboardRecommendations.module.css";

type Recommendation = {
  id: number;
  title: string;
  description: string;
  priority: string;
};

export default function DashboardRecommendations() {
  const [recommendations, setRecommendations] = useState<Recommendation[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetch("/api/campaign-recommedations/dashboard-panel")
      .then((res) => {
        if (!res.ok) throw new Error("Failed to fetch recommendations");
        return res.json();
      })
      .then((data) => {
        setRecommendations(data.recommendations);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  if (loading) return <div role="alert" aria-live="polite">Loading...</div>;
  if (error) return <div className={styles.error} role="alert" aria-live="assertive">Error: {error}</div>;

  return (
    <div>
      <h2>Campaign Recommendations</h2>
      <ul>
        {recommendations.map((rec) => (
          <li key={rec.id}>
            <strong>{rec.title}</strong>{" "}
            <span
              className={
                rec.priority === "high"
                  ? styles.high
                  : rec.priority === "medium"
                  ? styles.medium
                  : styles.low
              }
            >
              ({rec.priority})
            </span>
            <br />
            {rec.description}
          </li>
        ))}
      </ul>
    </div>
  );
}

import React, { Component, ErrorInfo } from 'react';
import { logger } from '@/lib/logger';

interface Props {
  children: React.ReactNode;
  fallback?: React.ReactNode;
}

interface State {
  hasError: boolean;
  error?: Error;
}

export class ErrorBoundary extends Component<Props, State> {
  public state: State = {
    hasError: false,
  };

  public static getDerivedStateFromError(error: Error): State {
    return { hasError: true, error };
  }

  public componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    logger.error('React Error Boundary caught an error:', {
      error: error.message,
      stack: error.stack,
      componentStack: errorInfo.componentStack,
    });
  }

  public render() {
    if (this.state.hasError) {
      if (this.props.fallback) {
        return this.props.fallback;
      }

      return (
        <div className="error-boundary" role="alert" aria-live="assertive">
          <h2>Something went wrong</h2>
          <p>Our team has been notified. Please try again later.</p>
          {process.env.NODE_ENV === 'development' && (
            <details>
              <summary>Error Details</summary>
              <pre>{this.state.error?.message}</pre>
              <pre>{this.state.error?.stack}</pre>
            </details>
          )}
          <button
            onClick={() => this.setState({ hasError: false })}
            className="retry-button"
          >
            Retry
          </button>
        </div>
      );
    }

    return this.props.children;
  }
}

