import { useState, useEffect, useCallback } from 'react';
import { usePathname } from 'next/navigation';
import { useTranslations } from 'next-intl';
import { TranslationService } from '@/lib/services/translationService';
import { Locale } from '@/types/i18n';

interface Message {
  id: string;
  senderId: string;
  senderName: string;
  originalText: string;
  translatedText: string;
  originalLanguage: string;
  timestamp: Date;
}

interface AutoTranslateMessageProps {
  className?: string;
  senderId: string;
  senderName: string;
  message: Message;
  showOriginal?: boolean;
}

const AutoTranslateMessage = ({
  message,
  showOriginal = false,
  className = '',
}: AutoTranslateMessageProps) => {
  return (
    <div className={`flex flex-col ${className}`}>
      <div className="flex items-center gap-2">
        <span className="font-semibold">{message.senderName}</span>
        <span className="text-xs text-gray-500">
          {new Date(message.timestamp).toLocaleTimeString()}
        </span>
      </div>
      <div className="message-content">
        <p className="text-gray-900">{message.translatedText}</p>
        {showOriginal && message.originalText !== message.translatedText && (
          <p className="text-sm text-gray-500 mt-1">
            Original ({message.originalLanguage}): {message.originalText}
          </p>
        )}
      </div>
    </div>
  );
};

export default function TranslatedChat() {
  const t = useTranslations('chat');
  const pathname = usePathname();
  const currentLocale = pathname?.split('/')[1] as Locale || 'en';
  
  const [messages, setMessages] = useState<Message[]>([]);
  const [newMessage, setNewMessage] = useState('');
  const [showOriginal, setShowOriginal] = useState(false);
  const [isTranslating, setIsTranslating] = useState(false);
  
  const translationService = new TranslationService();

  const sendMessage = useCallback(async () => {
    if (!newMessage.trim()) return;

    setIsTranslating(true);
    try {
      // Detect the language of the message
      const detectedLanguage = await translationService.detectLanguage(newMessage);
      
      // Translate the message to the recipient's language
      const { translatedText } = await translationService.translateMessage(
        newMessage,
        currentLocale,
        detectedLanguage as Locale
      );

      const message: Message = {
        id: Date.now().toString(),
        senderId: 'current-user-id', // Replace with actual user ID
        senderName: 'Current User', // Replace with actual user name
        originalText: newMessage,
        translatedText,
        originalLanguage: detectedLanguage,
        timestamp: new Date(),
      };

      setMessages(prev => [...prev, message]);
      setNewMessage('');
    } catch (error) {
      console.error('Error sending message:', error);
      // Show error notification
    } finally {
      setIsTranslating(false);
    }
  }, [newMessage, currentLocale]);

  return (
    <div className="flex flex-col h-full">
      {/* Messages List */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages.map((message) => (
          <AutoTranslateMessage
            key={message.id}
            message={message}
            senderId={message.senderId}
            senderName={message.senderName}
            showOriginal={showOriginal}
          />
        ))}
      </div>

      {/* Translation Toggle */}
      <div className="px-4 py-2 border-t border-gray-200">
        <label className="flex items-center space-x-2">
          <input
            type="checkbox"
            checked={showOriginal}
            onChange={(e) => setShowOriginal(e.target.checked)}
            className="form-checkbox"
          />
          <span>{t('showOriginalText')}</span>
        </label>
      </div>

      {/* Message Input */}
      <div className="border-t border-gray-200 p-4">
        <div className="flex space-x-4">
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder={t('typeMessage')}
            className="flex-1 border border-gray-300 rounded-lg px-4 py-2"
            disabled={isTranslating}
          />
          <button
            onClick={sendMessage}
            disabled={isTranslating || !newMessage.trim()}
            className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600 disabled:opacity-50"
          >
            {isTranslating ? t('translating') : t('send')}
          </button>
        </div>
      </div>
    </div>
  );
}

import { useState, useEffect, memo } from 'react';
import { useAutoTranslate } from '@/hooks/useAutoTranslate';
import { Locale } from '@/types/i18n';

interface TranslatedContentProps {
  content: string;
  sourceLocale?: Locale;
  className?: string;
  showOriginal?: boolean;
  as?: keyof JSX.IntrinsicElements;
}

export const TranslatedContent = memo(function TranslatedContent({
  content,
  sourceLocale,
  className = '',
  showOriginal = false,
  as: Component = 'span'
}: TranslatedContentProps) {
  const { translateContent } = useAutoTranslate();
  const [translatedText, setTranslatedText] = useState(content);
  const [isTranslating, setIsTranslating] = useState(true);

  useEffect(() => {
    let mounted = true;

    async function translate() {
      try {
        setIsTranslating(true);
        const translated = await translateContent(content, sourceLocale);
        if (mounted) {
          setTranslatedText(translated);
        }
      } catch (error) {
        console.error('Translation error:', error);
        if (mounted) {
          setTranslatedText(content); // Fallback to original content
        }
      } finally {
        if (mounted) {
          setIsTranslating(false);
        }
      }
    }

    translate();

    return () => {
      mounted = false;
    };
  }, [content, sourceLocale, translateContent]);

  return (
    <div className={`translated-content ${className}`}>
      <Component className={isTranslating ? 'opacity-50' : ''}>
        {translatedText}
      </Component>
      {showOriginal && translatedText !== content && (
        <Component className="text-sm text-gray-500 mt-1">
          {content}
        </Component>
      )}
    </div>
  );
});

