import { ReactNode } from 'react';
import { useFeatureAccess } from '@/hooks/useFeatureAccess';

interface FeatureGateProps {
  children: ReactNode;
  feature: string;
  fallback?: ReactNode;
  onDenied?: () => void;
}

export function FeatureGate({
  children,
  feature,
  fallback = null,
  onDenied
}: FeatureGateProps) {
  const { canAccessFeature } = useFeatureAccess();
  const hasAccess = canAccessFeature(feature);

  if (!hasAccess) {
    onDenied?.();
    return fallback;
  }

  return <>{children}</>;
}

import { ReactNode } from 'react';
import { usePermissions } from '@/hooks/usePermissions';
import { PermissionScope } from '@/lib/auth/permissions';
import { PermissionErrorFallback } from '@/components/errors/PermissionErrorFallback';

interface PermissionGateProps {
  children: ReactNode;
  permissions: PermissionScope | PermissionScope[];
  requireAll?: boolean;
  fallback?: ReactNode;
  onPermissionDenied?: () => void;
}

export function PermissionGate({
  children,
  permissions,
  requireAll = true,
  fallback,
  onPermissionDenied,
}: PermissionGateProps) {
  const { checkPermission, checkAllPermissions, checkAnyPermission } = usePermissions();

  const hasAccess = Array.isArray(permissions)
    ? requireAll
      ? checkAllPermissions(permissions)
      : checkAnyPermission(permissions)
    : checkPermission(permissions);

  if (!hasAccess) {
    onPermissionDenied?.();

    const fallbackNode =
      fallback ||
      (Array.isArray(permissions)
        ? permissions.length === 1
          ? <PermissionErrorFallback scope={permissions[0]} />
          : <PermissionErrorFallback />
        : <PermissionErrorFallback scope={permissions} />);

    return fallbackNode;
  }

  return <>{children}</>;
}

