import { doc, getDoc, onSnapshot } from "firebase/firestore";
import { auth, db } from "@/lib/firebase";
import { UserRole, PermissionScope, ROLE_PERMISSIONS, ResourceScope, ActionType } from "@/lib/auth/permissions";
import { PermissionManager } from "@/lib/auth/PermissionManager";
import { useRouter } from "next/navigation";

interface Subscription {
  tier: 'FREE' | 'PRO' | 'ENTERPRISE';
  status: 'active' | 'inactive' | 'cancelled';
  expiresAt?: string;
}

interface AuthContextType {
  user: User | null;
  role: UserRole | null;
  subscription: Subscription | null;
  isLoading: boolean;
  locale: string;
  hasPermission: (scope: PermissionScope) => boolean;
  hasResourcePermission: (resource: ResourceScope, action: ActionType) => boolean;
  hasFeatureAccess: (featureName: string) => boolean;
  signOut: () => Promise<void>;
  setLocale: (locale: string) => void;
}

export const AuthContext = createContext<AuthContextType>({
  user: null,
  role: null,
  subscription: null,
  isLoading: true,
  locale: 'en',
  hasPermission: () => false,
  hasResourcePermission: () => false,
  hasFeatureAccess: () => false,
  signOut: async () => {},
  setLocale: () => {},
});

interface AuthProviderProps {
  children: ReactNode;
}

export function AuthProvider({ children }: AuthProviderProps) {
  const [user, setUser] = useState<User | null>(null);
  const [role, setRole] = useState<UserRole | null>(null);
  const [subscription, setSubscription] = useState<Subscription | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [locale, setLocale] = useState(() => {
    if (typeof window !== 'undefined') {
      return localStorage.getItem('locale') || 'en';
    }
    return 'en';
  });
  const router = useRouter();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (user) => {
      setUser(user);
      if (user) {
        // Fetch user data from Firestore
        const userDoc = await getDoc(doc(db, "users", user.uid));
        const userData = userDoc.data();
        setRole(userData?.role || "USER");
        
        // Start subscription listener
        const subscriptionDoc = doc(db, "subscriptions", user.uid);
        const unsubscribeSubscription = onSnapshot(subscriptionDoc, (snapshot) => {
          const subData = snapshot.data() as Subscription | undefined;
          setSubscription(subData || { tier: 'FREE', status: 'active' });
        });

        return () => unsubscribeSubscription();
      } else {
        setRole(null);
        setSubscription(null);
      }
      setIsLoading(false);
    });
    return unsubscribe;
  }, []);

  const hasPermission = useCallback((scope: PermissionScope): boolean => {
    if (!role || !subscription) return false;
    const permissionManager = new PermissionManager(role, subscription.tier);
    return permissionManager.hasPermission(scope);
  }, [role, subscription]);

  const hasResourcePermission = useCallback((resource: ResourceScope, action: ActionType): boolean => {
    if (!role || !subscription) return false;
    const permissionManager = new PermissionManager(role, subscription.tier);
    const scope = `${resource}:${action}` as PermissionScope;
    return permissionManager.hasPermission(scope);
  }, [role, subscription]);

  const hasFeatureAccess = useCallback((featureName: string): boolean => {
    if (!role || !subscription) return false;
    const permissionManager = new PermissionManager(role, subscription.tier);
    return permissionManager.hasFeatureAccess(featureName);
  }, [role, subscription]);

  useEffect(() => {
    if (typeof window !== 'undefined') {
      localStorage.setItem('locale', locale);
    }
  }, [locale]);

  const signOut = async () => {
    await auth.signOut();
    router.push("/login");
  };

  return (
    <AuthContext.Provider value={{ 
      user, 
      role, 
      isLoading, 
      hasPermission, 
      hasResourcePermission, 
      signOut 
    }}>
      {children}
    </AuthContext.Provider>
  );
}

export const useAuth = () => useContext(AuthContext);

