import React, { createContext, useContext, useState, useEffect } from "react";

const supportedLanguages = ["en", "sw", "zu"];

const defaultLang = typeof navigator !== "undefined" && supportedLanguages.includes(navigator.language.split("-")[0])
  ? navigator.language.split("-")[0]
  : "en";

export const LanguageContext = createContext({
  lang: "en",
  setLang: (l: string) => {},
  t: (key: string) => key,
});

export const LanguageProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [lang, setLang] = useState<string>(() => {
    if (typeof window !== "undefined") {
      return localStorage.getItem("lang") || defaultLang || "en";
    }
    return "en";
  });
  const [messages, setMessages] = useState<Record<string, string>>({});

  useEffect(() => {
    async function loadLang() {
      try {
        if (!supportedLanguages.includes(lang)) {
          throw new Error("Unsupported language");
        }
        const mod = await import(`@/config/lang/${lang}`);
        setMessages(mod.default);
      } catch {
        const mod = await import("@/config/lang/en");
        setMessages(mod.default);
      }
    }
    loadLang();
    if (typeof window !== "undefined") {
      localStorage.setItem("lang", lang);
    }
  }, [lang]);

  function t(key: string) {
    return messages[key] || key;
  }

  return (
    <LanguageContext.Provider value={{ lang, setLang, t }}>
      {children}
    </LanguageContext.Provider>
  );
};

export function useLanguage() {
  return useContext(LanguageContext);
}

import React, { createContext, useContext } from "react";
import { useRole } from "@/context/RoleContext";
import { FeatureMap } from "@/config/FeatureMap";

type PlanTier = "free" | "pro" | "team" | "enterprise";
type FundingSource = "user" | "org";

interface PlanContextType {
  tier: PlanTier;
  source: FundingSource;
  features: string[];
  canUse: (feature: string) => boolean;
}

const PlanContext = createContext<PlanContextType>({
  tier: "free",
  source: "user",
  features: [],
  canUse: () => false
});

export const usePlan = () => useContext(PlanContext);

export const PlanProvider = ({ children }: { children: React.ReactNode }) => {
  const { plan, fundingSource, features, role } = useRole();

  const tierRank: Record<PlanTier, number> = {
    free: 0,
    pro: 1,
    team: 2,
    enterprise: 3
  };

  const canUse = (feature: string): boolean => {
    const config = FeatureMap[feature];
    if (!config) return false;

    const meetsTier = tierRank[plan] >= tierRank[config.requiredTier];

    const roleAllowed = Array.isArray(config.enabledForRoles)
      ? config.enabledForRoles.includes(role ?? "guest")
      : true;

    const orgOverride = config.orgFundable === true && fundingSource === "org";

    return (meetsTier && roleAllowed) || orgOverride;
  };

  return (
    <PlanContext.Provider value={{ tier: plan, source: fundingSource, features, canUse }}>
      {children}
    </PlanContext.Provider>
  );
};

import React, { createContext, useContext, useState, useEffect } from "react";

export type RealtimeState = {
  isOnline: boolean;
  micEnabled: boolean;
  camEnabled: boolean;
  canStartCall: boolean;
  canPost: boolean;
};

const defaultState: RealtimeState = {
  isOnline: true,
  micEnabled: false,
  camEnabled: false,
  canStartCall: false,
  canPost: true
};

const RealtimeContext = createContext<RealtimeState>(defaultState);

export const useRealtime = () => useContext(RealtimeContext);

export const RealtimeProvider = ({ children }: { children: React.ReactNode }) => {
  const [isOnline, setIsOnline] = useState(true);
  const [micEnabled, setMicEnabled] = useState(false);
  const [camEnabled, setCamEnabled] = useState(false);

  useEffect(() => {
    const handleOnline = () => setIsOnline(true);
    const handleOffline = () => setIsOnline(false);

    window.addEventListener("online", handleOnline);
    window.addEventListener("offline", handleOffline);

    return () => {
      window.removeEventListener("online", handleOnline);
      window.removeEventListener("offline", handleOffline);
    };
  }, []);

  useEffect(() => {
    const checkPermissions = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true, video: true });
        setMicEnabled(stream.getAudioTracks().length > 0);
        setCamEnabled(stream.getVideoTracks().length > 0);
        stream.getTracks().forEach(track => track.stop());
      } catch {
        setMicEnabled(false);
        setCamEnabled(false);
      }
    };

    checkPermissions();
  }, []);

  const realtimeState: RealtimeState = {
    isOnline,
    micEnabled,
    camEnabled,
    canStartCall: isOnline && (micEnabled || camEnabled),
    canPost: true
  };

  return (
    <RealtimeContext.Provider value={realtimeState}>
      {children}
    </RealtimeContext.Provider>
  );
};

