import { usePathname } from 'next/navigation';
import { useMemo } from 'react';
import { DEFAULT_CURRENCY, CURRENCIES, getAvailablePaymentMethods, formatCurrency } from '@/config/payment';
import { Locale } from '@/types/i18n';

export function usePaymentUtils() {
  const pathname = usePathname();
  const currentLocale = pathname?.split('/')[1] as Locale || 'en';
  
  const defaultCurrency = DEFAULT_CURRENCY[currentLocale];

  // Get user's country from browser or stored preference
  const userCountry = useMemo(() => {
    if (typeof navigator === 'undefined') return 'US';
    return navigator.languages?.[0]?.split('-')[1] || 'US';
  }, []);

  // Get available payment methods for the user's country and currency
  const availablePaymentMethods = useMemo(() => {
    return getAvailablePaymentMethods(userCountry, defaultCurrency);
  }, [userCountry, defaultCurrency]);

  // Format amount according to user's locale and currency
  const formatAmount = (amount: number, currency = defaultCurrency) => {
    return formatCurrency(amount, currency, currentLocale);
  };

  // Check if a payment method is available
  const isPaymentMethodAvailable = (methodId: string) => {
    return availablePaymentMethods.some(method => method.id === methodId);
  };

  // Get supported currencies for the user's country
  const supportedCurrencies = useMemo(() => {
    return Object.entries(CURRENCIES).filter(([code]) => 
      availablePaymentMethods.some(method => method.currencies.includes(code))
    );
  }, [availablePaymentMethods]);

  return {
    defaultCurrency,
    userCountry,
    availablePaymentMethods,
    formatAmount,
    isPaymentMethodAvailable,
    supportedCurrencies,
  };
}

import { useCallback, useMemo } from 'react';
import { useAuth } from '@/context/AuthContext';
import {
  ResourceScope,
  ActionType,
  PermissionScope
} from '@/lib/auth/permissions';
import { PermissionManager } from '@/lib/auth/PermissionManager';
import { FeaturePermissionGuard } from '@/lib/auth/guards/FeaturePermissionGuard';

export function usePermissions() {
  const { user, subscription } = useAuth();

  const permissionManager = useMemo(
    () =>
      new PermissionManager(
        user?.role || 'GUEST',
        subscription?.tier || 'FREE'
      ),
    [user?.role, subscription?.tier]
  );

  const hasPermission = useCallback(
    (permission: PermissionScope) =>
      permissionManager.hasPermission(permission),
    [permissionManager]
  );

  const hasResourcePermission = useCallback(
    (resource: ResourceScope, action: ActionType) =>
      permissionManager.hasResourcePermission(resource, action),
    [permissionManager]
  );

  const checkMultiple = useCallback(
    (permissions: PermissionScope[]) =>
      permissionManager.hasAllPermissions(permissions),
    [permissionManager]
  );

  const checkAny = useCallback(
    (permissions: PermissionScope[]) =>
      permissionManager.hasAnyPermission(permissions),
    [permissionManager]
  );

  const guardResource = useCallback(
    (
      resource: ResourceScope,
      action: ActionType,
      fallback?: React.ReactNode
    ) => {
      const hasAccess = hasResourcePermission(resource, action);
      return {
        hasAccess,
        fallback: hasAccess ? null : fallback || null
      };
    },
    [hasResourcePermission]
  );

  const guardScope = useCallback(
    (scope: PermissionScope, fallback?: React.ReactNode) => {
      const hasAccess = hasPermission(scope);
      return {
        hasAccess,
        fallback: hasAccess ? null : fallback || null
      };
    },
    [hasPermission]
  );

  const role = user?.role || 'GUEST';

  return {
    hasPermission,
    hasResourcePermission,
    checkMultiple,
    checkAny,
    guardResource,
    guardScope,
    role
  };
}

export function useResourcePermissions(resource: ResourceScope) {
  const { hasResourcePermission } = usePermissions();

  return {
    canRead: useCallback(
      () => hasResourcePermission(resource, 'read'),
      [resource, hasResourcePermission]
    ),
    canWrite: useCallback(
      () => hasResourcePermission(resource, 'write'),
      [resource, hasResourcePermission]
    ),
    canDelete: useCallback(
      () => hasResourcePermission(resource, 'delete'),
      [resource, hasResourcePermission]
    ),
    canManage: useCallback(
      () => hasResourcePermission(resource, 'manage'),
      [resource, hasResourcePermission]
    )
  };
}

