import { Redis } from '@upstash/redis';
import { logger } from '../logger';

const redis = Redis.fromEnv();

interface CacheOptions {
  ttl?: number; // Time to live in seconds
  invalidateOnMutation?: boolean;
}

export const cacheKey = (path: string, params?: Record<string, any>) => {
  const sortedParams = params
    ? Object.keys(params)
        .sort()
        .map(key => `${key}=${params[key]}`)
        .join('&')
    : '';

  return `api:${path}${sortedParams ? `?${sortedParams}` : ''}`;
};

export async function getCachedResponse<T>(
  key: string,
  fetchFn: () => Promise<T>,
  options: CacheOptions = {}
): Promise<T> {
  const { ttl = 60, invalidateOnMutation = true } = options;

  try {
    // Try to get from cache first
    const cached = await redis.get<T>(key);
    if (cached) {
      return cached;
    }

    // If not in cache, fetch fresh data
    const fresh = await fetchFn();

    // Cache the fresh data
    await redis.set(key, fresh, {
      ex: ttl,
    });

    return fresh;
  } catch (error) {
    logger.error('Cache error:', { error, key });
    // On cache error, fall back to fresh fetch
    return fetchFn();
  }
}

export async function invalidateCache(pattern: string): Promise<void> {
  try {
    const keys = await redis.keys(pattern);
    if (keys.length > 0) {
      await redis.del(...keys);
    }
  } catch (error) {
    logger.error('Cache invalidation error:', { error, pattern });
  }
}

import { NextResponse } from 'next/server';
import { ZodError } from 'zod';
import { logger } from '../logger';

export class APIError extends Error {
  constructor(
    message: string,
    public statusCode: number = 400,
    public code?: string
  ) {
    super(message);
    this.name = 'APIError';
  }
}

export const handleAPIError = (error: unknown) => {
  if (error instanceof APIError) {
    return NextResponse.json(
      {
        error: error.message,
        code: error.code,
      },
      { status: error.statusCode }
    );
  }

  if (error instanceof ZodError) {
    return NextResponse.json(
      {
        error: 'Validation Error',
        details: error.errors,
      },
      { status: 400 }
    );
  }

  logger.error('Unhandled API Error:', error);

  return NextResponse.json(
    { error: 'Internal Server Error' },
    { status: 500 }
  );
};

export const validateRequestBody = async <T>(
  req: Request,
  schema: { parse: (data: unknown) => T }
): Promise<T> => {
  try {
    const body = await req.json();
    return schema.parse(body);
  } catch (error) {
    throw error instanceof ZodError
      ? error
      : new APIError('Invalid request body', 400);
  }
};

import { UserRole, SubscriptionTier, PermissionScope, ROLE_PERMISSIONS, ROLE_HIERARCHY, SUBSCRIPTION_FEATURES } from './permissions';

interface Feature {
  name: string;
  requiredPermissions: PermissionScope[];
  requiredTiers: SubscriptionTier[];
}

const FEATURE_MAP: { [key: string]: Feature } = {
  'create-listing': {
    name: 'create-listing',
    requiredPermissions: ['listings:write'],
    requiredTiers: ['FREE', 'PRO', 'ENTERPRISE']
  },
  'advanced-analytics': {
    name: 'advanced-analytics',
    requiredPermissions: ['analytics:read'],
    requiredTiers: ['PRO', 'ENTERPRISE']
  },
  'bulk-operations': {
    name: 'bulk-operations',
    requiredPermissions: ['listings:write', 'listings:delete'],
    requiredTiers: ['ENTERPRISE']
  }
};

export class PermissionManager {
  constructor(
    private userRole: UserRole,
    private subscriptionTier: SubscriptionTier
  ) {}

  private getInheritedRoles(role: UserRole): UserRole[] {
    return ROLE_HIERARCHY[role];
  }

  private getRolePermissions(role: UserRole): PermissionScope[] {
    const inheritedRoles = this.getInheritedRoles(role);
    const allRoles = [role, ...inheritedRoles];
    
    return allRoles.flatMap(r => ROLE_PERMISSIONS[r]);
  }

  private getSubscriptionPermissions(tier: SubscriptionTier): PermissionScope[] {
    return SUBSCRIPTION_FEATURES[tier];
  }

  hasPermission(scope: PermissionScope): boolean {
    const rolePermissions = this.getRolePermissions(this.userRole);
    const subscriptionPermissions = this.getSubscriptionPermissions(this.subscriptionTier);
    
    return rolePermissions.includes(scope) || subscriptionPermissions.includes(scope);
  }

  hasAllPermissions(scopes: PermissionScope[]): boolean {
    return scopes.every(scope => this.hasPermission(scope));
  }

  hasAnyPermission(scopes: PermissionScope[]): boolean {
    return scopes.some(scope => this.hasPermission(scope));
  }

  getEffectivePermissions(): PermissionScope[] {
    const rolePermissions = this.getRolePermissions(this.userRole);
    const subscriptionPermissions = this.getSubscriptionPermissions(this.subscriptionTier);
    
    return [...new Set([...rolePermissions, ...subscriptionPermissions])];
  }

  hasFeatureAccess(featureName: string): boolean {
    const feature = FEATURE_MAP[featureName];
    if (!feature) return false;

    const hasRequiredPermissions = this.hasAllPermissions(feature.requiredPermissions);
    const hasRequiredTier = feature.requiredTiers.includes(this.subscriptionTier);

    return hasRequiredPermissions && hasRequiredTier;
  }

  getAvailableFeatures(): string[] {
    return Object.keys(FEATURE_MAP).filter(featureName => this.hasFeatureAccess(featureName));
  }

  canUpgrade(targetTier: SubscriptionTier): boolean {
    const tierOrder: SubscriptionTier[] = ['FREE', 'BASIC', 'PRO', 'ENTERPRISE'];
    const currentIndex = tierOrder.indexOf(this.subscriptionTier);
    const targetIndex = tierOrder.indexOf(targetTier);
    
    return targetIndex > currentIndex;
  }
}

import { PermissionManager } from '../PermissionManager';
import { PermissionScope } from '../permissions';

export class ApiPermissionGuard {
  constructor(private permissionManager: PermissionManager) {}

  canAccessEndpoint(method: string, path: string): boolean {
    const scope = this.mapEndpointToPermission(method, path);
    return this.permissionManager.hasPermission(scope);
  }

  private mapEndpointToPermission(method: string, path: string): PermissionScope {
    // Map REST endpoints to permissions
    const mapping: Record<string, PermissionScope> = {
      'GET /api/listings': 'listings:read',
      'POST /api/listings': 'listings:write',
      'PUT /api/listings': 'listings:write',
      'DELETE /api/listings': 'listings:delete',
      'GET /api/users': 'users:read',
      'POST /api/users': 'users:write',
      'GET /api/analytics': 'analytics:read',
      'GET /api/settings': 'settings:read',
      'PUT /api/settings': 'settings:write'
    };

    return mapping[`${method} ${path}`] || 'api:read';
  }
}

import { PermissionManager } from '../PermissionManager';
