export class FeaturePermissionGuard {
  constructor(private permissionManager: PermissionManager) {}

  canAccessFeature(feature: string): boolean {
    const featurePermissions: Record<string, PermissionScope[]> = {
      'translation': ['translation:read', 'translation:write'],
      'analytics': ['analytics:read'],
      'api': ['api:read', 'api:write'],
      'marketplace': ['marketplace:read', 'marketplace:write'],
      'chat': ['chat:read', 'chat:write'],
      'reports': ['reports:read', 'reports:write'],
      'integrations': ['integrations:read', 'integrations:write']
    };

    const requiredPermissions = featurePermissions[feature] || [];
    return this.permissionManager.hasAnyPermission(requiredPermissions);
  }

  getAccessibleFeatures(): string[] {
    return Object.entries(this.getFeaturePermissionMap())
      .filter(([_, permissions]) => this.permissionManager.hasAnyPermission(permissions))
      .map(([feature]) => feature);
  }

  private getFeaturePermissionMap(): Record<string, PermissionScope[]> {
    return {
      'translation': ['translation:read', 'translation:write'],
      'analytics': ['analytics:read'],
      'api': ['api:read', 'api:write'],
      'marketplace': ['marketplace:read', 'marketplace:write'],
      'chat': ['chat:read', 'chat:write'],
      'reports': ['reports:read', 'reports:write'],
      'integrations': ['integrations:read', 'integrations:write']
    };
  }
}

