  await sendEmail({
    to,
    subject: `[Pulse Connect] ${data.priority.toUpperCase()}: ${data.title}`,
    html: htmlContent,
  });
}

// Utility function for error messages
export function getErrorMessage(code: string): string {
  switch (code) {
    case "auth/email-already-in-use":
      return "This email is already in use.";
    case "auth/invalid-email":
      return "Invalid email address.";
    case "auth/weak-password":
      return "Password should be at least 6 characters.";
    case "auth/user-not-found":
      return "No user found with this email.";
    case "auth/wrong-password":
      return "Incorrect password.";
    default:
      return "An error occurred. Please try again.";
  }
}

import { logger } from './logger';

export class AppError extends Error {
  public readonly code: string;
  public readonly statusCode: number;
  public readonly isOperational: boolean;
  public readonly context?: Record<string, unknown>;

  constructor(
    message: string,
    code = 'INTERNAL_ERROR',
    statusCode = 500,
    isOperational = true,
    context?: Record<string, unknown>
  ) {
    super(message);
    this.name = this.constructor.name;
    this.code = code;
    this.statusCode = statusCode;
    this.isOperational = isOperational;
    this.context = context;
    Error.captureStackTrace(this, this.constructor);
  }
}

export const logError = (
  error: Error | AppError,
  additionalContext?: Record<string, unknown>
) => {
  const errorObject: Record<string, unknown> = {
    name: error.name,
    message: error.message,
    stack: error.stack,
  };

  if (error instanceof AppError) {
    errorObject.code = error.code;
    errorObject.statusCode = error.statusCode;
    errorObject.isOperational = error.isOperational;
    errorObject.context = error.context;
  }

  if (additionalContext) {
    errorObject.additionalContext = additionalContext;
  }

  logger.error(errorObject, 'An error occurred');
};

// Predefined error types
export const ErrorTypes = {
  VALIDATION_ERROR: { code: 'VALIDATION_ERROR', statusCode: 400 },
  UNAUTHORIZED: { code: 'UNAUTHORIZED', statusCode: 401 },
  FORBIDDEN: { code: 'FORBIDDEN', statusCode: 403 },
  NOT_FOUND: { code: 'NOT_FOUND', statusCode: 404 },
  CONFLICT: { code: 'CONFLICT', statusCode: 409 },
  INTERNAL_ERROR: { code: 'INTERNAL_ERROR', statusCode: 500 },
} as const;

// src/lib/fetcher.ts
export async function fetcher<T>(url: string): Promise<T> {
  const res = await fetch(url);
  if (!res.ok) {
    const err = new Error(`Failed to fetch ${url}: ${res.statusText}`);
    // @ts-expect-error attach status for richer error handling
    err.status = res.status;
    throw err;
  }
  return res.json();
}

