/**
 * Utility function to generate localized strings for supported locales.
 * @param value - The string to localize.
 * @returns A LocalizedString object with translations for all locales.
 */
function localize(value: string): LocalizedString {
  return LOCALES.reduce<LocalizedString>((acc, locale) => {
    acc[locale] = value;
    return acc;
  }, {} as LocalizedString);
}

