import { Ratelimit } from '@upstash/ratelimit';
import { redis } from './redis';
import { logger } from './logger';

// Different rate limit configurations
export const rateLimitConfigs = {
  DEFAULT: {
    requests: 60,
    duration: '60 s',
    message: 'Too many requests, please try again later.'
  },
  AUTH: {
    requests: 5,
    duration: '60 s',
    message: 'Too many authentication attempts, please try again later.'
  },
  API: {
    requests: 100,
    duration: '60 s',
    message: 'API rate limit exceeded.'
  }
} as const;

type RateLimitType = keyof typeof rateLimitConfigs;

// Create rate limiters for different purposes
const createRateLimiter = (type: RateLimitType) => {
  const config = rateLimitConfigs[type];
  return new Ratelimit({
    redis,
    limiter: Ratelimit.slidingWindow(config.requests, config.duration),
    analytics: true,
    prefix: `ratelimit:${type.toLowerCase()}`,
  });
};

// Export rate limiters
export const rateLimiters = {
  default: createRateLimiter('DEFAULT'),
  auth: createRateLimiter('AUTH'),
  api: createRateLimiter('API'),
};

// Rate limit checker with logging
export async function checkRateLimit(
  identifier: string,
  type: RateLimitType = 'DEFAULT'
) {
  const limiter = rateLimiters[type.toLowerCase() as keyof typeof rateLimiters];
  const config = rateLimitConfigs[type];

  try {
    const result = await limiter.limit(identifier);
    
    if (!result.success) {
      logger.warn({
        message: 'Rate limit exceeded',
        identifier,
        type,
        remaining: result.remaining,
        limit: result.limit,
        reset: result.reset,
      });
    }

    return {
      success: result.success,
      limit: result.limit,
      remaining: result.remaining,
      reset: result.reset,
      message: config.message
    };
  } catch (error) {
    logger.error({
      message: 'Rate limit check failed',
      error,
      identifier,
      type,
    });
    
    // Fail open if rate limiting is unavailable
    return {
      success: true,
      limit: config.requests,
      remaining: config.requests,
      reset: 0,
      message: 'Rate limit check failed'
    };
  }
}

import type { RealtimeState } from "@/context/RealtimeContext";

type RealtimeContextExtras = {
  role: string;
  org: string;
  language: string;
};

export const getRealtimeMessages = (
  state: RealtimeState,
  extras?: Partial<RealtimeContextExtras>
): string[] => {
  const { role, org, language } = extras || {};
  const messages: string[] = [];

  if (!state.isOnline) {
    messages.push(
      language === "fr"
        ? "Vous êtes hors ligne. Les appels sont désactivés."
        : "You're offline. Call features are disabled."
    );
  }

  if (!state.micEnabled) {
    messages.push(
      role === "team-lead"
        ? "Microphone access denied. Team leads require audio to manage sessions."
        : "Microphone access denied."
    );
  }

  if (!state.camEnabled) {
    messages.push(
      org === "Marketing"
        ? "Camera blocked. Video posts may not be available."
        : "Camera blocked."
    );
  }

  return messages;
};

