import { generateVerificationToken } from '@/lib/utils/token';
import { hashPassword } from '@/lib/utils/password';
import { getRedisClient } from '@/lib/redis';
import { MAX_ADMINS } from '@/config/admin';
import { PermissionManager } from '@/lib/auth/PermissionManager';
import { UserRole, PermissionScope } from '@/lib/auth/types';

interface AdminUser {
  email: string;
  fullName: string;
  password: string;
}

// Helper function to check permissions
async function checkAdminPermission(role: UserRole, requiredPermission: PermissionScope): Promise<void> {
  const permissionManager = new PermissionManager(role);
  if (!permissionManager.hasPermission(requiredPermission)) {
    throw new Error('Unauthorized: Insufficient permissions');
  }
}

export async function createAdminUser(userData: AdminUser, role: UserRole) {
  await checkAdminPermission(role, 'admin:manage');

  const client = getRedisClient();

  // Check if max admins reached
  const adminCount = await client.get<string>('admin:count');
  if (adminCount && parseInt(adminCount) >= MAX_ADMINS) {
    throw new Error('Maximum number of admins reached');
  }

  // Check if email already exists
  const emailExists = await client.get<string>(`admin:email:${userData.email}`);
  if (emailExists) {
    const error = new Error('An account with this email already exists') as Error & { code: string };
    error.code = 'EMAIL_ALREADY_EXISTS';
    throw error;
  }

  // Generate verification code
  const verificationCode = generateVerificationToken();
  
  // Hash password
  const hashedPassword = await hashPassword(userData.password);

  // Store user data with verification pending status
  const adminData = {
    email: userData.email,
    fullName: userData.fullName,
    password: hashedPassword,
    verified: 'false',
    createdAt: Date.now().toString(),
  };

  // Store the data
  await client.set(`admin:pending:${verificationCode}`, JSON.stringify(adminData));
  await client.set(`admin:email:${userData.email}`, verificationCode);
  await client.expire(`admin:pending:${verificationCode}`, 24 * 60 * 60); // 24 hours expiry
  await client.expire(`admin:email:${userData.email}`, 24 * 60 * 60);
  
  return { verificationCode };
}

export async function verifyAdminUser(verificationCode: string) {
  const client = getRedisClient();

  // Get pending admin data
  const pendingAdmin = await client.get<string>(`admin:pending:${verificationCode}`);
  if (!pendingAdmin) {
    throw new Error('Invalid or expired verification code');
  }

  const adminData = JSON.parse(pendingAdmin);
  
  // Check if max admins reached before confirming
  const adminCount = await client.get<string>('admin:count') || '0';
  if (parseInt(adminCount) >= MAX_ADMINS) {
    throw new Error('Maximum number of admins reached');
  }

  // Store confirmed admin data
  await client.hset(`admin:${adminData.email}`, {
    ...adminData,
    verified: 'true',
    verifiedAt: Date.now().toString(),
  });

  // Increment admin count
  await client.incr('admin:count');

  // Clean up pending data
  await client.del(`admin:pending:${verificationCode}`);
  await client.del(`admin:email:${adminData.email}`);

  return { email: adminData.email };
}

export async function getAdminByEmail(email: string) {
  const client = getRedisClient();
  const adminData = await client.hgetall(`admin:${email}`);
  
  if (!adminData || Object.keys(adminData).length === 0) {
    return null;
  }

  return {
    ...adminData,
    verified: adminData.verified === 'true',
  };
}

export class AdminService {
  /**
   * Generate a unique admin code
   */
  private static async generateUniqueCode(): Promise<string> {
    const code = nanoid(ADMIN_CODE_LENGTH).toUpperCase();
    const exists = await redis.exists(`${ADMIN_KEY_PREFIX}${code}`);
    
    if (exists) {
      return this.generateUniqueCode();
    }
    
    return code;
  }

  /**
   * Create a new admin code and send it via email
   */
  static async createAdminCode(email: string): Promise<string> {
    try {
      // Check current admin count
      const currentCount = await redis.get<number>(ADMIN_COUNT_KEY) || 0;
      
      if (currentCount >= ADMIN_MAX_COUNT) {
        throw new AppError(
          'Maximum number of admins reached',
          'MAX_ADMINS_REACHED',
          403,
          true
        );
      }

      // Generate unique code
      const adminCode = await this.generateUniqueCode();
      
      // Store admin details
      await redis.set(`${ADMIN_KEY_PREFIX}${adminCode}`, {
        email,
        createdAt: new Date().toISOString(),
        lastUsed: null
      });

      // Increment admin count
      await redis.incr(ADMIN_COUNT_KEY);

      // Send email with admin code
      await sendEmail({
        to: email,
        subject: 'Your Pulse Connect Admin Access Code',
        template: 'admin-code',
        variables: {
          adminCode,
          validityPeriod: '∞ (Never expires)',
          maxUses: '∞ (Unlimited uses)'
        }
      });

      logger.info({ email }, 'New admin code generated and sent');
      
      return adminCode;
    } catch (error) {
      logger.error({ error, email }, 'Failed to create admin code');
      throw error;
    }
  }

  /**
   * Validate an admin code
   */
  static async validateCode(code: string): Promise<boolean> {
    try {
      const adminDetails = await redis.get(`${ADMIN_KEY_PREFIX}${code}`);
      
      if (!adminDetails) {
        return false;
      }

      // Update last used timestamp
      await redis.set(`${ADMIN_KEY_PREFIX}${code}`, {
        ...adminDetails,
        lastUsed: new Date().toISOString()
      });

      return true;
    } catch (error) {
      logger.error({ error, code }, 'Failed to validate admin code');
      return false;
    }
  }

  /**
   * List all admin codes
   */
  static async listAdminCodes(): Promise<Array<{ code: string; email: string; createdAt: string; lastUsed: string | null }>> {
    try {
      const keys = await redis.keys(`${ADMIN_KEY_PREFIX}*`);
      const adminCodes = await Promise.all(
        keys.map(async (key) => {
          const details = await redis.get(key);
          return {
            code: key.replace(ADMIN_KEY_PREFIX, ''),
            ...details
          };
        })
      );

      return adminCodes;
    } catch (error) {
      logger.error({ error }, 'Failed to list admin codes');
      throw error;
    }
  }

  /**
   * Revoke an admin code
   */
  static async revokeCode(code: string, role: UserRole): Promise<boolean> {
    await checkAdminPermission(role, 'admin:manage');

    const exists = await redis.exists(`${ADMIN_KEY_PREFIX}${code}`);
    
    if (!exists) {
      return false;
    }

    await redis.del(`${ADMIN_KEY_PREFIX}${code}`);
    await redis.decr(ADMIN_COUNT_KEY);

    logger.info({ code }, 'Admin code revoked');
    return true;
  }
}

import { getRedisClient } from '@/lib/redis';

interface Announcement {
  id: string;
  title: string;
  message: string;
  createdAt: string;
  createdBy: string;
  priority: 'low' | 'medium' | 'high';
}

export async function createAnnouncement(announcement: Omit<Announcement, 'id' | 'createdAt'>) {
  const client = getRedisClient();
  const id = `announcement:${Date.now()}`;
  const createdAt = new Date().toISOString();

  // Store the announcement
  await client.hset(`announcements:${id}`, {
    ...announcement,
    id,
    createdAt,
  });

  // Add to the announcements set
  await client.zadd('announcements:list', {
    score: Date.now(),
    member: id,
  });

  return {
    id,
    ...announcement,
    createdAt,
  };
}

export async function getAnnouncements(page = 1, limit = 10) {
  const client = getRedisClient();
  const start = (page - 1) * limit;
  const end = start + limit - 1;

  // Get announcement IDs sorted by creation time
  const announcementIds = await client.zrange('announcements:list', start, end, {
    rev: true, // Reverse order (newest first)
  });

  if (!announcementIds.length) {
    return {
      announcements: [],
      total: 0,
      page,
      limit,
    };
  }

  // Get announcement details in parallel
  const announcements = await Promise.all(
    announcementIds.map(async (id) => {
      const data = await client.hgetall(`announcements:${id}`);
      return data;
    })
  );

  // Get total count
  const total = await client.zcard('announcements:list');

  return {
    announcements: announcements.filter(Boolean),
    total,
    page,
    limit,
  };
}

export async function getAllUserEmails() {
  const client = getRedisClient();
  const userEmails = new Set<string>();
  let cursor = 0;
  
  do {
    // Scan user keys in batches to handle large datasets efficiently
    const [nextCursor, keys] = await client.scan(cursor, {
      match: 'user:*@*',
      count: 1000
    });
    cursor = parseInt(nextCursor);

    // Filter verified user keys
    const verifiedUserKeys = keys.filter(key => !key.includes('pending:') && !key.includes('email:'));

    // Get user data in parallel for this batch
    const batchEmails = await Promise.all(
      verifiedUserKeys.map(async (key) => {
        const data = await client.hgetall(key);
        if (data && data.verified === 'true') {
          return data.email;
        }
        return null;
      })
    );

    // Add valid emails to the set
    batchEmails.filter(Boolean).forEach(email => userEmails.add(email));

  } while (cursor !== 0); // Continue until we've scanned all keys

  return Array.from(userEmails);
}

// Queue an email for sending
export async function queueAnnouncementEmail(data: {
  to: string;
  title: string;
  message: string;
  priority: 'low' | 'medium' | 'high';
  createdBy: string;
  announcementId: string;
}) {
  const client = getRedisClient();
  const emailId = `email:${Date.now()}:${Math.random().toString(36).slice(2)}`;

  // Store email data
  await client.hset(emailId, {
    ...data,
    status: 'pending',
    createdAt: Date.now().toString(),
    retries: '0',
  });

  // Add to email queue
  await client.zadd('email:queue', {
    score: Date.now(),
    member: emailId,
  });

  return emailId;
}

// Process email queue in batches
export async function processEmailQueue(batchSize: number = 100) {
  const client = getRedisClient();
  let processed = 0;
  let failed = 0;

  // Get batch of emails to process
  const emailIds = await client.zrange('email:queue', 0, batchSize - 1);

  for (const emailId of emailIds) {
    try {
      const emailData = await client.hgetall(emailId);
      if (!emailData) continue;

      await sendAnnouncementEmail(emailData.to, {
        title: emailData.title,
        message: emailData.message,
        priority: emailData.priority as 'low' | 'medium' | 'high',
        createdBy: emailData.createdBy,
      });

      // Mark as sent
      await client.hset(emailId, { status: 'sent', sentAt: Date.now().toString() });
      await client.zrem('email:queue', emailId);
      processed++;
    } catch (error) {
      const retries = parseInt(await client.hget(emailId, 'retries') || '0');
      
      if (retries >= 3) {
        // Mark as failed after 3 retries
        await client.hset(emailId, { 
          status: 'failed',
          error: error.message,
          failedAt: Date.now().toString()
        });
        await client.zrem('email:queue', emailId);
      } else {
        // Increment retry count and push to back of queue
        await client.hset(emailId, { retries: (retries + 1).toString() });
        await client.zadd('email:queue', {
          score: Date.now() + (retries + 1) * 300000, // 5 minutes delay per retry
          member: emailId,
        });
      }
      failed++;
    }
  }

  return { processed, failed, remaining: await client.zcard('email:queue') };
}

import { db } from '@/firebase/config';
import {
  collection,
  doc,
  addDoc,
  getDoc,
  updateDoc,
  query,
  where,
  orderBy,
  getDocs,
} from 'firebase/firestore';
import { CreateBooking, Booking, BookingStatus, Listing } from './types';
import { logger } from '@/lib/logger';
import { APIError } from '@/lib/api/errors';

export async function createBooking(
  booking: CreateBooking
): Promise<Booking> {
  try {
    // 1. Check if listing exists
    const listingRef = doc(db, 'listings', booking.listingId);
    const listingSnap = await getDoc(listingRef);
    
    if (!listingSnap.exists()) {
      throw new APIError('Listing not found', 404);
    }

    // 2. Check for date conflicts
    const conflictingBookings = await getDocs(
      query(
        collection(db, 'bookings'),
        where('listingId', '==', booking.listingId),
        where('status', '==', 'confirmed'),
        where('checkIn', '<=', booking.checkOut),
        where('checkOut', '>=', booking.checkIn)
      )
    );

    if (!conflictingBookings.empty) {
      throw new APIError('Selected dates are not available', 400);
    }

    // 3. Create the booking
    const bookingData = {
      ...booking,
      status: 'pending' as BookingStatus,
      paymentStatus: 'pending',
      createdAt: new Date(),
      updatedAt: new Date(),
    };

    const docRef = await addDoc(collection(db, 'bookings'), bookingData);
    
    return {
      id: docRef.id,
      ...bookingData,
    };
  } catch (error) {
    logger.error('Booking creation failed:', error);
    if (error instanceof APIError) {
      throw error;
    }
    throw new APIError('Failed to create booking');
  }
}

export async function updateBookingStatus(
  bookingId: string,
  status: BookingStatus,
  paymentId?: string
): Promise<void> {
  try {
    const bookingRef = doc(db, 'bookings', bookingId);
    const booking = await getDoc(bookingRef);

    if (!booking.exists()) {
      throw new APIError('Booking not found', 404);
    }

    await updateDoc(bookingRef, {
      status,
      paymentId,
      updatedAt: new Date(),
    });
  } catch (error) {
    logger.error('Booking status update failed:', error);
    if (error instanceof APIError) {
      throw error;
    }
    throw new APIError('Failed to update booking status');
  }
}

export async function getBookingsByUser(userId: string): Promise<Booking[]> {
  try {
    const bookingsQuery = query(
      collection(db, 'bookings'),
      where('userId', '==', userId),
      orderBy('createdAt', 'desc')
    );

    const snapshot = await getDocs(bookingsQuery);
    return snapshot.docs.map(doc => ({
      id: doc.id,
      ...doc.data(),
    } as Booking));
  } catch (error) {
    logger.error('Failed to fetch user bookings:', error);
    throw new APIError('Failed to fetch bookings');
  }
}

export async function createListing(listing: Listing): Promise<void> {
  try {
    const listingData = {
      ...listing,
      createdAt: new Date(),
      updatedAt: new Date(),
    };

    await addDoc(collection(db, 'listings'), listingData);
  } catch (error) {
    logger.error('Listing creation failed:', error);
    throw new APIError('Failed to create listing');
  }
}

