import nodemailer from 'nodemailer';
import { env } from '@/env';
import { logger } from '@/lib/logger';

// Create reusable transporter
const transporter = nodemailer.createTransport({
  host: env.SMTP_HOST,
  port: env.SMTP_PORT,
  secure: env.SMTP_PORT === 465,
  auth: {
    user: env.SMTP_USER,
    pass: env.SMTP_PASSWORD,
  },
});

// Verify connection configuration
transporter.verify((error) => {
  if (error) {
    logger.error('SMTP connection error:', error);
  } else {
    logger.info('SMTP server is ready to send emails');
  }
});

interface EmailOptions {
  to: string;
  subject: string;
  template: string;
  context: Record<string, any>;
  attachments?: Array<{
    filename: string;
    content: Buffer | string;
    contentType?: string;
  }>;
}

export async function sendEmail({
  to,
  subject,
  template,
  context,
  attachments = [],
}: EmailOptions): Promise<boolean> {
  try {
    const info = await transporter.sendMail({
      from: `Pulse Connect <${env.EMAIL_FROM}>`,
      to,
      subject,
      template,
      context,
      attachments,
    });

    logger.info('Email sent:', { messageId: info.messageId, to, subject });
    return true;
  } catch (error) {
    logger.error('Email sending failed:', { error, to, subject });
    return false;
  }
}

import { Redis } from '@upstash/redis';
import { RiskAssessment, RiskResult, RiskFactor, RiskLevel } from './types';
import { logger } from '@/lib/logger';

const redis = Redis.fromEnv();

const RISK_THRESHOLDS = {
  low: 30,
  medium: 60,
  high: 80,
};

const TIME_WINDOWS = {
  rapid_booking: 3600, // 1 hour
  failed_attempts: 86400, // 24 hours
};

export async function assessRisk(
  assessment: RiskAssessment
): Promise<RiskResult> {
  const factors: RiskFactor[] = [];
  let score = 0;

  try {
    // 1. Check for rapid bookings
    const recentBookings = await redis.zcount(
      `bookings:${assessment.userId}`,
      Date.now() - TIME_WINDOWS.rapid_booking * 1000,
      Date.now()
    );
    
    if (recentBookings > 3) {
      factors.push('rapid_booking');
      score += 20;
    }

    // 2. Check for multiple failed payment attempts
    const failedAttempts = await redis.zcount(
      `failed_payments:${assessment.userId}`,
      Date.now() - TIME_WINDOWS.failed_attempts * 1000,
      Date.now()
    );

    if (failedAttempts > 2) {
      factors.push('failed_attempts');
      score += 30;
    }

    // 3. Check for high-value transactions
    if (assessment.amount > 1000) {
      factors.push('high_value');
      score += 15;
    }

    // 4. Check for IP/location mismatch
    const lastIP = await redis.get(`last_ip:${assessment.userId}`);
    if (lastIP && lastIP !== assessment.ipAddress) {
      factors.push('ip_mismatch');
      score += 10;
    }

    // 5. Check card country against IP location (if available)
    if (assessment.cardCountry && assessment.cardCountry !== 'US') {
      factors.push('card_country_mismatch');
      score += 25;
    }

    // 6. Check for new accounts
    const userCreatedAt = await redis.get(`user_created:${assessment.userId}`);
    if (!userCreatedAt || Date.now() - Number(userCreatedAt) < 86400000) {
      factors.push('new_account');
      score += 20;
    }

    // Determine risk level
    let level: RiskLevel = 'low';
    if (score >= RISK_THRESHOLDS.high) level = 'critical';
    else if (score >= RISK_THRESHOLDS.medium) level = 'high';
    else if (score >= RISK_THRESHOLDS.low) level = 'medium';

    // Store assessment result
    await redis.set(`risk:${assessment.sessionId}`, {
      score,
      factors,
      level,
      timestamp: Date.now(),
    });

    return {
      level,
      factors,
      score,
      allowTransaction: level !== 'critical',
      requiresReview: level === 'high' || level === 'critical',
    };
  } catch (error) {
    logger.error('Risk assessment failed:', error);
    return {
      level: 'high',
      factors: ['system_error'],
      score: 75,
      allowTransaction: false,
      requiresReview: true,
    };
  }
}

import { z } from 'zod';

