import { PaymentRequest, PaymentResponse } from './types';
import { env } from '@/env';
import { logger } from '@/lib/logger';

const BASE_URL = 'https://api.paystack.co';

export async function createPaystackPayment(
  request: PaymentRequest
): Promise<PaymentResponse> {
  try {
    const response = await fetch(`${BASE_URL}/transaction/initialize`, {
      method: 'POST',
      headers: {
        Authorization: `Bearer ${env.PAYSTACK_SECRET_KEY}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        email: request.email,
        amount: Math.round(request.amount * 100),
        currency: request.currency,
        callback_url: request.returnUrl,
        metadata: {
          ...request.metadata,
          gateway: 'paystack',
        },
      }),
    });

    if (!response.ok) {
      throw new Error(`Paystack error: ${response.statusText}`);
    }

    const data = await response.json();

    return {
      success: true,
      sessionId: data.data.reference,
      url: data.data.authorization_url,
      metadata: data.data.metadata,
    };
  } catch (error) {
    logger.error('Paystack payment creation failed:', error);
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Payment creation failed',
    };
  }
}

export async function verifyPaystackPayment(reference: string): Promise<boolean> {
  try {
    const response = await fetch(
      `${BASE_URL}/transaction/verify/${reference}`,
      {
        headers: {
          Authorization: `Bearer ${env.PAYSTACK_SECRET_KEY}`,
        },
      }
    );

    if (!response.ok) {
      throw new Error(`Paystack verification error: ${response.statusText}`);
    }

    const data = await response.json();
    return data.data.status === 'success';
  } catch (error) {
    logger.error('Paystack payment verification failed:', error);
    return false;
  }
}

