            unit_amount: Math.round(request.amount * 100),
          },
          quantity: 1,
        },
      ],
      success_url: `${request.returnUrl}?session_id={CHECKOUT_SESSION_ID}`,
      cancel_url: `${request.returnUrl}?canceled=true`,
    });

    return {
      success: true,
      sessionId: session.id,
      url: session.url!,
      metadata: session.metadata,
    };
  } catch (error) {
