interface TranslationQualityMetrics {
  confidence: number;
  fluency: number;
  adequacy: number;
  consistency: number;
}

interface TranslationMemoryEntry {
  sourceText: string;
  targetText: string;
  locale: string;
  context: string;
  usage: number;
  lastUsed: Date;
  quality: TranslationQualityMetrics;
}

export class TranslationQualityService {
  private memoryStore: Map<string, TranslationMemoryEntry> = new Map();
  private readonly minConfidenceThreshold = 0.7;
  private readonly maxMemoryEntries = 10000;

  private calculateMemoryKey(sourceText: string, targetLocale: string): string {
    return `${sourceText}:${targetLocale}`;
  }

  private calculateTextSimilarity(text1: string, text2: string): number {
    // Implement Levenshtein distance or similar algorithm
    const maxLength = Math.max(text1.length, text2.length);
    if (maxLength === 0) return 1.0;
    
    const distance = this.levenshteinDistance(text1, text2);
    return 1 - distance / maxLength;
  }

  private levenshteinDistance(text1: string, text2: string): number {
    if (text1.length === 0) return text2.length;
    if (text2.length === 0) return text1.length;

    const matrix: number[][] = [];

    for (let i = 0; i <= text1.length; i++) {
      matrix[i] = [i];
    }

    for (let j = 0; j <= text2.length; j++) {
      matrix[0][j] = j;
    }

    for (let i = 1; i <= text1.length; i++) {
      for (let j = 1; j <= text2.length; j++) {
        if (text1[i - 1] === text2[j - 1]) {
          matrix[i][j] = matrix[i - 1][j - 1];
        } else {
          matrix[i][j] = Math.min(
            matrix[i - 1][j - 1] + 1,
            matrix[i][j - 1] + 1,
            matrix[i - 1][j] + 1
          );
        }
      }
    }

    return matrix[text1.length][text2.length];
  }

  private evaluateTranslationQuality(
    sourceText: string,
    translatedText: string,
    existingTranslations: TranslationMemoryEntry[]
  ): TranslationQualityMetrics {
    // Calculate base confidence
    let confidence = 0.8; // Default confidence

    // Adjust confidence based on existing translations
    if (existingTranslations.length > 0) {
      const similarities = existingTranslations.map(entry => 
        this.calculateTextSimilarity(entry.targetText, translatedText)
      );
      const maxSimilarity = Math.max(...similarities);
      confidence = (confidence + maxSimilarity) / 2;
    }

    // Calculate fluency (based on text patterns and common structures)
    const fluency = this.calculateFluencyScore(translatedText);

    // Calculate adequacy (how well the meaning is preserved)
    const adequacy = this.calculateAdequacyScore(sourceText, translatedText);

    // Calculate consistency with existing translations
    const consistency = this.calculateConsistencyScore(translatedText, existingTranslations);

    return {
      confidence,
      fluency,
      adequacy,
      consistency
    };
  }

  private calculateFluencyScore(text: string): number {
    // Implement fluency scoring logic
    // This could check for grammar patterns, sentence structure, etc.
    return 0.85; // Placeholder implementation
  }

  private calculateAdequacyScore(source: string, translation: string): number {
    // Implement adequacy scoring logic
    // This could check for preservation of named entities, numbers, etc.
    return 0.9; // Placeholder implementation
  }

  private calculateConsistencyScore(
    translation: string,
    existingTranslations: TranslationMemoryEntry[]
  ): number {
    if (existingTranslations.length === 0) return 1.0;

    const similarities = existingTranslations.map(entry =>
      this.calculateTextSimilarity(entry.targetText, translation)
    );

    return similarities.reduce((a, b) => a + b, 0) / similarities.length;
  }

  addToMemory(
    sourceText: string,
    targetText: string,
    locale: string,
    context: string = 'general'
  ): void {
    const key = this.calculateMemoryKey(sourceText, locale);
    const existingEntries = Array.from(this.memoryStore.values())
      .filter(entry => entry.locale === locale);

    const quality = this.evaluateTranslationQuality(sourceText, targetText, existingEntries);

    if (quality.confidence >= this.minConfidenceThreshold) {
      const entry: TranslationMemoryEntry = {
        sourceText,
        targetText,
        locale,
        context,
        usage: 1,
        lastUsed: new Date(),
        quality
      };

      this.memoryStore.set(key, entry);

      // Cleanup old entries if needed
      if (this.memoryStore.size > this.maxMemoryEntries) {
        this.cleanupOldEntries();
      }
    }
  }

  findInMemory(sourceText: string, targetLocale: string): TranslationMemoryEntry | null {
    const key = this.calculateMemoryKey(sourceText, targetLocale);
    const entry = this.memoryStore.get(key);

    if (entry) {
      entry.usage += 1;
      entry.lastUsed = new Date();
      return entry;
    }

    return null;
  }

  private cleanupOldEntries(): void {
    const entries = Array.from(this.memoryStore.entries())
      .sort(([, a], [, b]) => {
        // Sort by usage and last used date
        if (a.usage !== b.usage) {
          return a.usage - b.usage;
        }
        return a.lastUsed.getTime() - b.lastUsed.getTime();
      });

    // Remove oldest, least used entries
    const entriesToRemove = Math.floor(this.maxMemoryEntries * 0.2); // Remove 20%
    entries.slice(0, entriesToRemove).forEach(([key]) => {
      this.memoryStore.delete(key);
    });
  }

  getQualityMetrics(sourceText: string, targetLocale: string): TranslationQualityMetrics | null {
    const entry = this.findInMemory(sourceText, targetLocale);
    return entry ? entry.quality : null;
  }

  getSimilarTranslations(text: string, locale: string, threshold: number = 0.8): TranslationMemoryEntry[] {
    return Array.from(this.memoryStore.values())
      .filter(entry => {
        if (entry.locale !== locale) return false;
        const similarity = this.calculateTextSimilarity(entry.sourceText, text);
        return similarity >= threshold;
      })
      .sort((a, b) => b.quality.confidence - a.quality.confidence);
  }
}

import { Locale } from '@/types/i18n';

export interface TranslationResult {
  translatedText: string;
  detectedSourceLanguage?: string;
  confidence?: number;
}

export class TranslationService {
  private apiKey: string;
  private endpoint: string;

  constructor() {
    this.apiKey = process.env.AZURE_TRANSLATOR_KEY || '';
    this.endpoint = process.env.AZURE_TRANSLATOR_ENDPOINT || '';
  }

  async translateMessage(
    text: string,
    targetLanguage: Locale,
    sourceLanguage?: Locale
  ): Promise<TranslationResult> {
    const url = new URL(`${this.endpoint}/translate`);
    url.searchParams.append('api-version', '3.0');
    url.searchParams.append('to', targetLanguage);
    if (sourceLanguage) {
      url.searchParams.append('from', sourceLanguage);
    }

    try {
      const response = await fetch(url.toString(), {
        method: 'POST',
        headers: {
          'Ocp-Apim-Subscription-Key': this.apiKey,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify([{ text }]),
      });

      if (!response.ok) {
        throw new Error(`Translation failed: ${response.statusText}`);
      }

      const [result] = await response.json();
      return {
        translatedText: result.translations[0].text,
        detectedSourceLanguage: result.detectedLanguage?.language,
        confidence: result.detectedLanguage?.score,
      };
    } catch (error) {
      console.error('Translation error:', error);
      throw error;
    }
  }

  async detectLanguage(text: string): Promise<string> {
    const url = new URL(`${this.endpoint}/detect`);
    url.searchParams.append('api-version', '3.0');

    try {
      const response = await fetch(url.toString(), {
        method: 'POST',
        headers: {
          'Ocp-Apim-Subscription-Key': this.apiKey,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify([{ text }]),
      });

      if (!response.ok) {
        throw new Error(`Language detection failed: ${response.statusText}`);
      }

      const [result] = await response.json();
      return result.language;
    } catch (error) {
      console.error('Language detection error:', error);
      throw error;
    }
  }
}

import { getRedisClient } from '@/lib/redis';
import { UserProfile, UserProfileUpdate } from '@/types/UserProfile';
import { nanoid } from 'nanoid';

export async function getUserProfile(userId: string): Promise<UserProfile | null> {
  const client = getRedisClient();
  const profile = await client.hgetall(`user:profile:${userId}`);
  
  if (!profile || Object.keys(profile).length === 0) {
    return null;
  }

  // Parse JSON fields
  return {
    ...profile,
    location: profile.location ? JSON.parse(profile.location) : undefined,
    communicationPreferences: JSON.parse(profile.communicationPreferences),
    socialMedia: profile.socialMedia ? JSON.parse(profile.socialMedia) : undefined,
    interests: profile.interests ? JSON.parse(profile.interests) : undefined,
    privacySettings: JSON.parse(profile.privacySettings),
  } as UserProfile;
}

export async function updateUserProfile(
  userId: string,
  updates: UserProfileUpdate
): Promise<UserProfile> {
  const client = getRedisClient();
  const existingProfile = await getUserProfile(userId);

  if (!existingProfile) {
    throw new Error('Profile not found');
  }

  // Prepare updates by stringifying objects
  const updatesToSave = {
    ...updates,
    location: updates.location ? JSON.stringify(updates.location) : undefined,
    communicationPreferences: updates.communicationPreferences
      ? JSON.stringify(updates.communicationPreferences)
      : undefined,
    socialMedia: updates.socialMedia
      ? JSON.stringify(updates.socialMedia)
      : undefined,
    interests: updates.interests ? JSON.stringify(updates.interests) : undefined,
    privacySettings: updates.privacySettings
      ? JSON.stringify(updates.privacySettings)
      : undefined,
    updatedAt: new Date().toISOString(),
  };

  // Remove undefined values
  Object.keys(updatesToSave).forEach(
    (key) => updatesToSave[key] === undefined && delete updatesToSave[key]
  );

  // Save updates
  await client.hset(`user:profile:${userId}`, updatesToSave);

  // Return updated profile
  return getUserProfile(userId);
}

export async function createUserProfile(
  email: string,
  fullName: string,
  preferredLanguage: string = 'en'
): Promise<UserProfile> {
  const client = getRedisClient();
  const userId = nanoid();

  const newProfile: UserProfile = {
    id: userId,
    email,
    fullName,
    preferredLanguage,
    communicationPreferences: {
      email: true,
      sms: false,
      inApp: true,
    },
    privacySettings: {
      profileVisibility: 'public',
      showEmail: false,
      showPhone: false,
      showLocation: false,
    },
    createdAt: new Date().toISOString(),
    updatedAt: new Date().toISOString(),
  };

  // Prepare profile for Redis storage
  const profileToSave = {
    ...newProfile,
    communicationPreferences: JSON.stringify(newProfile.communicationPreferences),
    privacySettings: JSON.stringify(newProfile.privacySettings),
  };

  // Save profile
  await client.hset(`user:profile:${userId}`, profileToSave);

  // Create email index for lookup
  await client.set(`user:email:${email}`, userId);

  return newProfile;
}

export async function getUserIdByEmail(email: string): Promise<string | null> {
  const client = getRedisClient();
  return client.get(`user:email:${email}`);
}

export async function deleteUserProfile(userId: string): Promise<void> {
  const client = getRedisClient();
  const profile = await getUserProfile(userId);

  if (!profile) {
    throw new Error('Profile not found');
  }

  // Delete profile and email index
  await client.del(`user:profile:${userId}`);
  await client.del(`user:email:${profile.email}`);
}

export async function updateUserLanguage(
  userId: string,
  language: string
): Promise<void> {
  const client = getRedisClient();
  await client.hset(`user:profile:${userId}`, { preferredLanguage: language });
}

import { Locale } from '@/types/i18n';

export class VoiceTranslationService {
  private recognition: SpeechRecognition | null = null;
  private synthesis: SpeechSynthesis;
  private voices: Map<Locale, SpeechSynthesisVoice[]> = new Map();

  constructor() {
    if (typeof window !== 'undefined') {
      // Initialize speech recognition
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      if (SpeechRecognition) {
        this.recognition = new SpeechRecognition();
        this.recognition.continuous = false;
        this.recognition.interimResults = false;
      }

      // Initialize speech synthesis
      this.synthesis = window.speechSynthesis;
      this.loadVoices();
    }
  }

  private async loadVoices(): Promise<void> {
    return new Promise((resolve) => {
      const loadVoicesHandler = () => {
        const allVoices = this.synthesis.getVoices();
        
        // Group voices by language
        allVoices.forEach(voice => {
          const locale = voice.lang.split('-')[0] as Locale;
          if (!this.voices.has(locale)) {
            this.voices.set(locale, []);
          }
          this.voices.get(locale)?.push(voice);
        });

        resolve();
      };

      // Chrome loads voices asynchronously
      if (this.synthesis.onvoiceschanged !== undefined) {
        this.synthesis.onvoiceschanged = loadVoicesHandler;
      }

      // For browsers that load voices synchronously
      loadVoicesHandler();
    });
  }

  async startListening(locale: Locale): Promise<string> {
    if (!this.recognition) {
      throw new Error('Speech recognition not supported in this browser');
    }

    return new Promise((resolve, reject) => {
      this.recognition!.lang = locale;
      
      this.recognition!.onresult = (event) => {
        const transcript = event.results[0][0].transcript;
        resolve(transcript);
      };

      this.recognition!.onerror = (event) => {
        reject(new Error(`Speech recognition error: ${event.error}`));
      };

      this.recognition!.start();
    });
  }

  stopListening(): void {
    if (this.recognition) {
      this.recognition.stop();
    }
  }

  async speak(text: string, locale: Locale): Promise<void> {
    await this.loadVoices();

    const utterance = new SpeechSynthesisUtterance(text);
    const voices = this.voices.get(locale) || [];
    
    if (voices.length > 0) {
      // Use the first available voice for the locale
      utterance.voice = voices[0];
    }
    
    utterance.lang = locale;

    return new Promise((resolve, reject) => {
      utterance.onend = () => resolve();
      utterance.onerror = (error) => reject(error);
      this.synthesis.speak(utterance);
    });
  }

  pause(): void {
    this.synthesis.pause();
  }

  resume(): void {
    this.synthesis.resume();
  }

  cancel(): void {
    this.synthesis.cancel();
  }

  getVoices(locale: Locale): SpeechSynthesisVoice[] {
    return this.voices.get(locale) || [];
  }

  isRecognitionSupported(): boolean {
    return !!this.recognition;
  }

  isSynthesisSupported(): boolean {
    return 'speechSynthesis' in window;
  }
}

