import { NextResponse } from 'next/server';
import type { NextRequest } from 'next/server';
import { getToken } from 'next-auth/jwt';
import { Ratelimit } from '@upstash/ratelimit';
import { Redis } from '@upstash/redis';
import createI18nMiddleware from 'next-intl/middleware';
import { LOCALE_REGION_MAPPING, RTL_LOCALES } from '@/config/lang';
import { 
  UserRole, 
  PermissionScope, 
  ROLE_PERMISSIONS, 
  ResourceScope, 
  ActionType 
} from '@/lib/auth/permissions';
import { PermissionManager } from '@/lib/auth/PermissionManager';
import { ApiPermissionGuard } from '@/lib/auth/guards/ApiPermissionGuard';

// Create a new ratelimiter that allows 10 requests per 10 seconds
const ratelimit = new Ratelimit({
  redis: Redis.fromEnv(),
  limiter: Ratelimit.slidingWindow(10, '10 s'),
});

// Create i18n middleware
const i18nMiddleware = createI18nMiddleware({
  locales: Object.keys(LOCALE_REGION_MAPPING),
  defaultLocale: 'en',
  localeDetection: true,
  localePrefix: 'always'
});

export async function middleware(request: NextRequest) {
  // Handle internationalization first
  const i18nResponse = await i18nMiddleware(request);
  if (i18nResponse) return i18nResponse;

  const response = NextResponse.next();

  // 1. Rate Limiting
  const ip = request.headers.get('x-forwarded-for') ?? '127.0.0.1';
  const { success, limit, reset, remaining } = await ratelimit.limit(ip);

  response.headers.set('X-RateLimit-Limit', limit.toString());
  response.headers.set('X-RateLimit-Remaining', remaining.toString());
  response.headers.set('X-RateLimit-Reset', reset.toString());

  if (!success) {
    response.headers.set('Content-Type', 'application/json');
    return new Response(
      JSON.stringify({ error: 'Too many requests' }),
      { headers: response.headers, status: 429 }
    );
  }

  // 2. CORS Headers for API routes
  if (request.nextUrl.pathname.startsWith('/api')) {
    response.headers.set('Access-Control-Allow-Origin', process.env.NEXT_PUBLIC_FRONTEND_URL || '*');
    response.headers.set('Access-Control-Allow-Methods', 'GET, POST, PUT, DELETE, OPTIONS');
    response.headers.set('Access-Control-Allow-Headers', 'Content-Type, Authorization');
  }

  // 3. Authentication and Authorization Check for Protected Routes
  if (
    request.nextUrl.pathname.startsWith('/api') &&
    !request.nextUrl.pathname.startsWith('/api/auth') &&
    !request.nextUrl.pathname.startsWith('/api/public')
  ) {
    try {
      const token = await getToken({ req: request });
      if (!token) {
        response.headers.set('Content-Type', 'application/json');
        return new Response(
          JSON.stringify({ error: 'Unauthorized' }),
          { headers: response.headers, status: 401 }
        );
      }

      // Add role to request headers for downstream handling
      if (token.role) {
        response.headers.set('X-User-Role', token.role as string);
      }

      // Initialize PermissionManager with user's role and subscription tier
      const permissionManager = new PermissionManager(
        token.role as UserRole,
        token.subscription?.tier || 'FREE'
      );
      
      const apiGuard = new ApiPermissionGuard(permissionManager);
      
      // Check if user can access the endpoint
      if (!apiGuard.canAccessEndpoint(request.method, request.nextUrl.pathname)) {
        response.headers.set('Content-Type', 'application/json');
        return new Response(
          JSON.stringify({ 
            error: 'Forbidden: Insufficient permissions',
            requiredRole: token.role,
            path: request.nextUrl.pathname,
            method: request.method
          }),
          { headers: response.headers, status: 403 }
        );
      }
    } catch (error) {
      console.error('Auth Error:', error);
      response.headers.set('Content-Type', 'application/json');
      return new Response(
        JSON.stringify({ error: 'Internal Server Error' }),
        { headers: response.headers, status: 500 }
      );
    }
  }

  // 4. Security Headers
  response.headers.set('X-DNS-Prefetch-Control', 'on');
  response.headers.set('Strict-Transport-Security', 'max-age=63072000; includeSubDomains; preload');
  response.headers.set('X-Frame-Options', 'SAMEORIGIN');
  response.headers.set('X-Content-Type-Options', 'nosniff');
  response.headers.set('Referrer-Policy', 'origin-when-cross-origin');
  response.headers.set('Permissions-Policy', 'camera=(), microphone=(), geolocation=()');

  return response;
}

export const config = {
  matcher: [
    '/api/:path*',
    '/((?!_next/static|_next/image|favicon.ico).*)',
  ],
};

import { NextResponse } from 'next/server';
import type { NextRequest } from 'next/server';
import { PermissionManager } from '@/lib/auth/PermissionManager';
import { ApiPermissionGuard } from '@/lib/auth/guards/ApiPermissionGuard';
import { getSession } from '@/lib/auth/session';

export async function permissionMiddleware(request: NextRequest) {
  const session = await getSession(request);
  
  if (!session) {
    return NextResponse.json(
      { error: 'Unauthorized' },
      { status: 401 }
    );
  }

  const permissionManager = new PermissionManager(
    session.user.role,
    session.subscription?.tier || 'FREE'
  );
  
  const apiGuard = new ApiPermissionGuard(permissionManager);
  
  if (!apiGuard.canAccessEndpoint(request.method, request.nextUrl.pathname)) {
    return NextResponse.json(
      { error: 'Insufficient permissions' },
      { status: 403 }
    );
  }

  return NextResponse.next();
}

export function withPermissionMiddleware(handler: Function, options: {
  requiredPermissions?: string[];
  methods?: string[];
}) {
  return async function(req: NextRequest) {
    const result = await permissionMiddleware(req);
    
    if (result.status === 401 || result.status === 403) {
      return result;
    }
    
    return handler(req);
  };
}

