import { z } from "zod";

/**
 * Zod schema for product creation + retrieval.
 * createdAt will default to `new Date()` if not provided.
 */
export const ProductSchema = z.object({
  id: z.string().uuid().optional(),
  name: z.string().min(1),
  price: z.number().nonnegative(),
  createdAt: z.date().default(() => new Date())
});

export type Product = z.infer<typeof ProductSchema>;

interface SocialMedia {
  platform: 'twitter' | 'linkedin' | 'facebook' | 'instagram';
  url: string;
}

interface Location {
  country: string;
  city?: string;
  timezone: string;
}

interface UserProfile {
  id: string;
  email: string;
  fullName: string;
  displayName?: string;
  avatar?: string;
  bio?: string;
  location?: Location;
  phoneNumber?: string;
  preferredLanguage: string;
  communicationPreferences: {
    email: boolean;
    sms: boolean;
    inApp: boolean;
  };
  socialMedia?: SocialMedia[];
  profession?: string;
  company?: string;
  interests?: string[];
  privacySettings: {
    profileVisibility: 'public' | 'private' | 'connections';
    showEmail: boolean;
    showPhone: boolean;
    showLocation: boolean;
  };
  createdAt: string;
  updatedAt: string;
}

export type UserProfileUpdate = Partial<Omit<UserProfile, 'id' | 'email' | 'createdAt' | 'updatedAt'>>;

export type { UserProfile, SocialMedia, Location };

import { z } from 'zod';

// Base API Response Schema
const BaseResponseSchema = z.object({
  success: z.boolean(),
  timestamp: z.string().datetime(),
});

// Error Response Schema
export const APIErrorSchema = BaseResponseSchema.extend({
  success: z.literal(false),
  error: z.object({
    message: z.string(),
    code: z.string().optional(),
    details: z.unknown().optional(),
  }),
});

// Paginated Response Schema
