export function getBrowserRegion(): string | null {
  if (typeof navigator === "undefined") return null;
  const [, region] = navigator.language.split("-");
  return region?.toUpperCase() ?? null;
}

/**
 * Calculates platform commission and net payout.
 * Used in payment processing to ensure Pulse Connect earns sustainably.
 */

export const PLATFORM_COMMISSION_RATE = 0.15;

export interface CommissionResult {
  gross: number;
  net: number;
  fee: number;
}

export const calculateCommission = (amount: number): CommissionResult => {
  const fee = amount * PLATFORM_COMMISSION_RATE;
  const net = amount - fee;
  return {
    gross: amount,
    net,
    fee,
  };
};

