import { PaymentMetadata } from '../../types/payment';
import { calculateCommission } from './commission';
import { processMpesaPayment } from '../../lib/services/payment/mpesa';
import { processPaystackPayment } from '../../lib/services/payment/paystack';
import { processStripePayment } from '../../lib/services/payment/stripe';

export const processPayment = async (meta: PaymentMetadata): Promise<{ success: boolean }> => {
  const { net, fee } = calculateCommission(meta.amount);
  const adjustedMeta = { ...meta, amount: net, commission: fee };

  switch (meta.gateway) {
    case 'mpesa':
      return await processMpesaPayment(adjustedMeta);
    case 'paystack':
      return await processPaystackPayment(adjustedMeta);
    case 'stripe':
      return await processStripePayment(adjustedMeta);
    default:
      return { success: false };
  }
};

export type Gateway = 'mpesa' | 'paystack' | 'stripe' | 'paypal';

export const selectGateway = (region: string): Gateway => {
  const normalized = region.trim().toLowerCase();
  if (['ke', 'kenya'].includes(normalized)) return 'mpesa';
  if (['ng', 'nigeria'].includes(normalized)) return 'paystack';
  if (['us', 'ca', 'uk'].includes(normalized)) return 'stripe';
  return 'paypal'; // fallback for global
};

